window.episodesChunk1 = window.episodesChunk1 || [{"id":1,"title":"Difficult Customer","level":"Elementary","mp3":"/audio/englishpod_0001pb.mp3","transcript":"https://ia600408.us.archive.org/10/items/englishpod_all/englishpod_0001.html","transcriptData":[{"speaker":"A","text":"Good evening. My name is Fabio. I'll be your waiter for tonight. May I take your order?"},{"speaker":"B","text":"No, I'm still working on it. This menu isn't even in English. What's good here?"},{"speaker":"A","text":"For you sir, I would recommend  spaghetti and meatballs."},{"speaker":"B","text":"Does it come with coke and fries?"},{"speaker":"A","text":"It comes with either soup or salad and a complimentary glass of wine, sir."},{"speaker":"B","text":"I'll go with the spaghetti and meatballs, salad and the wine."},{"speaker":"A","text":"Excellent choice. Your order will be ready soon."},{"speaker":"B","text":"How soon is soon?"},{"speaker":"A","text":"Twenty minutes?"},{"speaker":"B","text":"You know what? I'll just go grab a burger across the street."}],"vocabData":{"key":[{"word":"grab","type":"principle verb, present simple","definition":"get quickly"},{"word":"go with","type":"phrase","definition":"to choose, pick"},{"word":"would recommend","type":"phrase","definition":"suggest"},{"word":"complimentary","type":"Adjective","definition":"free"},{"word":"still working on","type":"phrase","definition":"not yet completed, need more time"}],"supplementary":[{"word":"impatient","type":"phrase","definition":"uncomfortable waiting, wanting to go"},{"word":"fast food","type":"phrase","definition":"food prepared and served quickly"},{"word":"waitress","type":"common noun, singular","definition":"female server at a restaurant or bar"},{"word":"fancy","type":"Adjective","definition":"nice, expensive, up-scale"},{"word":"casual","type":"Adjective","definition":"relaxed, not dressy"}]}},{"id":2,"title":"Calling In Sick","level":"Elementary","mp3":"/audio/englishpod_0002pb.mp3","transcript":"https://ia600408.us.archive.org/10/items/englishpod_all/englishpod_0002.html","transcriptData":[{"speaker":"A","text":"Hello, Daniel speaking, how may I help you?"},{"speaker":"B","text":"Hi, Daniel, Julie here."},{"speaker":"A","text":"Hi, Julie, how are you?"},{"speaker":"B","text":"Actually, I'm feeling quite ill today."},{"speaker":"A","text":"I'm sorry to hear that. What's wrong?"},{"speaker":"B","text":"I think I'm coming down with the flu.  I have a headache, a sore throat a runny nose and I'm feeling slightly feverish."},{"speaker":"A","text":"I see... so you're calling in sick?"},{"speaker":"B","text":"Yes, I was hoping to take the day off to recover."},{"speaker":"A","text":"OK, then. Try and get some rest."}],"vocabData":{"key":[{"word":"was hoping","type":"phrase","definition":"to kindly expect"},{"word":"calling in sick","type":"phrase","definition":"report that you will not go to work because you are sick"},{"word":"coming down","type":"phrase","definition":"getting, acquiring"},{"word":"quite ill","type":"phrase","definition":"very sick"},{"word":"how may I help you?","type":"phrase","definition":"Polite offer to help"}],"supplementary":[{"word":"impolite","type":"Adjective","definition":"rude"},{"word":"permission","type":"common noun, singular","definition":"allow"},{"word":"symptom","type":"common noun, singular","definition":"a sign, indication"},{"word":"sick day","type":"phrase","definition":"day off because you're sick"},{"word":"make it into work","type":"phrase","definition":"go to work"}]}},{"id":3,"title":"Hotel Upgrade","level":"Elementary","mp3":"/audio/englishpod_0003pb.mp3","transcript":"https://ia600408.us.archive.org/10/items/englishpod_all/englishpod_0003.html","transcriptData":[{"speaker":"A","text":"Good afternoon.   What can    I do for  you?"},{"speaker":"B","text":"I'd like to check in please. I have a reservation under the name Anthony Roberts."},{"speaker":"A","text":"All right R.O.B.E.R.T.S... Oh, Mr. Roberts we've been expecting you… and here is your keycard to the presidential suite."},{"speaker":"B","text":"But there must be some mistake; my reservation was for a  standard room."},{"speaker":"A","text":"Are you sure? Let me double check ."},{"speaker":"B","text":"Yeah…Here, this is my confirmation number."},{"speaker":"A","text":"You're right Mr. Roberts, there seems to be a mix-up,  unfortunately we're overbooked at the moment ."},{"speaker":"B","text":"So…"},{"speaker":"A","text":"Not to worry.   We're  pleased to offer  you a complimentary upgrade."},{"speaker":"B","text":"Presidential suite baby!"}],"vocabData":{"key":[{"word":"check in","type":"principle verb, infinitive","definition":"register at a hotel or airport"},{"word":"under the name","type":"phrase","definition":"name used for the reservation"},{"word":"reservation","type":"common noun, singular","definition":"booking"},{"word":"confirmation","type":"common noun, singular","definition":"an approval, verification"},{"word":"mix-up","type":"common noun, singular","definition":"confusion"},{"word":"complimentary upgrade","type":"phrase","definition":"free improvement of quality"},{"word":"overbooked","type":"Adjective","definition":"full"}],"supplementary":[{"word":"check out","type":"verb","definition":"pay and leave a hotel"},{"word":"booking","type":"common noun, singular","definition":"reservation"},{"word":"concierge","type":"common noun, singular","definition":"hotel employee that helps guests"},{"word":"deluxe room","type":"common noun, singular","definition":"hotel room, better than standard"},{"word":"receipt","type":"common noun, singular","definition":"paper showing payment details"}]}},{"id":4,"title":"I Need An Assistant!","level":"Elementary","mp3":"/audio/englishpod_0004pb.mp3","transcript":"https://ia600408.us.archive.org/10/items/englishpod_all/englishpod_0004.html","transcriptData":[{"speaker":"A","text":"...like I told you before, we just don't have the resources to hire you an assistant."},{"speaker":"B","text":"I understand that, but the fact is we're   understaffed."},{"speaker":"A","text":"The timing is just not right.  The economy is bad, and it's too  risky  to take on  new staff."},{"speaker":"B","text":"Yeah, I guess you're   right.... here's an idea, what if we hire an intern?  She would take some of the weight off my shoulders."},{"speaker":"A","text":"She?"},{"speaker":"B","text":"Yeah, you know, a recent graduate. She could give me a hand with some of these projects  and we could keep our costs down."},{"speaker":"A","text":"That sounds reasonable...  let me see   what I can do."},{"speaker":"A","text":"Tony, I'd like to introduce you to your new assistant."},{"speaker":"B","text":"OK, great! Let's  meet her!"},{"speaker":"B","text":"Oh... hi... I'm Tony..."}],"vocabData":{"key":[{"word":"resources","type":"common noun, plural","definition":"money"},{"word":"understaffed","type":"Adjective","definition":"not enough people to do the job"},{"word":"the timing is just not right","type":"phrase","definition":"it is not a good time now"},{"word":"weight off my shoulders","type":"phrase","definition":"remove pressure or stress"},{"word":"give me a hand","type":"phrase","definition":"help"},{"word":"that sounds reasonable","type":"phrase","definition":"is OK"}],"supplementary":[{"word":"recruit","type":"principle verb, infinitive","definition":"hire"},{"word":"overworked","type":"Adjective","definition":"working too much"},{"word":"short staffed","type":"Adjective","definition":"not enough people to do the job"},{"word":"cut costs","type":"phrase","definition":"reduce spending"},{"word":"overstaffed","type":"Adjective","definition":"having too many employees"}]}},{"id":5,"title":"Cut In Line","level":"Elementary","mp3":"/audio/englishpod_0005pb.mp3","transcript":"https://ia600408.us.archive.org/10/items/englishpod_all/englishpod_0005.html","transcriptData":[{"speaker":"A","text":"I can't believe it took us two hours  to get here.  The traffic in New York  is unbelievable."},{"speaker":"B","text":"Yeah, but just relax honey, we're here and we're going on vacation.  In a few hours we'll be in Hawaii,  and you'll be on the golf course."},{"speaker":"A","text":"Oh no!  Look at that line!  It must be a mile long!  There's no way I'm waiting for another two hours."},{"speaker":"B","text":"Honey...  don't..."},{"speaker":"A","text":"Yeah..."},{"speaker":"A","text":"Says who?"},{"speaker":"A","text":"So sue me!"}],"vocabData":{"key":[{"word":"unbelievable","type":"Adjective","definition":"incredible, amazing"},{"word":"relax","type":"principle verb, imperative","definition":"calm down"},{"word":"be a mile long","type":"phrase","definition":"is very long"},{"word":"cut in line","type":"phrase","definition":"move in front of someone waiting in line"},{"word":"there's no way","type":"phrase","definition":"it's impossible"}],"supplementary":[{"word":"traffic jam","type":"phrase","definition":"many cars not moving on the road"},{"word":"a few hours from now","type":"phrase","definition":"soon, in a couple of hours"},{"word":"form a queue","type":"phrase","definition":"to get in line"},{"word":"jump the queue","type":"phrase","definition":"cut in line ( Br E)"},{"word":"stand in line","type":"phrase","definition":"wait in line"},{"word":"line up","type":"phrase","definition":"form a line"}]}},{"id":6,"title":"Road Trip","level":"Elementary","mp3":"/audio/englishpod_0006pb.mp3","transcript":"https://ia600408.us.archive.org/10/items/englishpod_all/englishpod_0006.html","transcriptData":[{"speaker":"A","text":"So,  are we all ready to go?"},{"speaker":"B","text":"Yup, I think so. The car's packed; we have munchies and music, and the map's in the car."},{"speaker":"A","text":"Did you get the camera?"},{"speaker":"B","text":"Got it!  Did you fill up the tank?"},{"speaker":"A","text":"Yup, it's all set."},{"speaker":"B","text":"You're sure we're not forgetting anything?"},{"speaker":"A","text":"I'm sure... we've  got all our bases covered."},{"speaker":"B","text":"Well… let's   get going  then! I love road trips!"},{"speaker":"B","text":"Um...  do you think we can make a pit stop?"},{"speaker":"A","text":"But we've  only been on the road for ten minutes."},{"speaker":"B","text":"I know,  but I forgot to go to the bathroom before we left."}],"vocabData":{"key":[{"word":"all ready to go","type":"phrase","definition":"prepared, ready to leave"},{"word":"packed","type":"Adjective","definition":"full, everything is inside"},{"word":"munchies","type":"common noun, non-variable","definition":"snacks, food"},{"word":"fill up the tank","type":"phrase","definition":"put gas in the car"},{"word":"all set","type":"phrase","definition":"ready"},{"word":"got all the bases covered","type":"phrase","definition":"have everything ready"},{"word":"get going","type":"phrase","definition":"start, leave, depart"},{"word":"road trip","type":"phrase","definition":"long car trip"},{"word":"pit stop","type":"phrase","definition":"quick stop for food or gas"}],"supplementary":[{"word":"trunk","type":"common noun, singular","definition":"the luggage area of a car"},{"word":"passenger","type":"common noun, singular","definition":"person in a car, not the driver"},{"word":"freeway","type":"common noun, singular","definition":"highway"},{"word":"rental car","type":"phrase","definition":"a car you rent, a car you pay to borrow"},{"word":"highway toll","type":"phrase","definition":"fee paid for using the highway"}]}},{"id":7,"title":"Virus!","level":"Elementary","mp3":"/audio/englishpod_0007pb.mp3","transcript":"https://ia600408.us.archive.org/10/items/englishpod_all/englishpod_0007.html","transcriptData":[{"speaker":"A","text":"Oh great! This stupid computer froze again!  That’s the third time today!  Hey Samuel, can you come take a look at my PC?  It’s acting up again.  It must have a virus or something."},{"speaker":"B","text":"Just give me a second;  I’ll be right up."},{"speaker":"B","text":"I ran a virus scan on your computer,  and it turns out that you have a lot of infected files!"},{"speaker":"A","text":"But I’m quite careful when I’m browsing the internet,  I have no idea how I could have picked up a virus."},{"speaker":"B","text":"Well, you have to make sure that your anti-virus software is updated regularly; yours wasn't up to date,  that's probably what was causing your problems."},{"speaker":"A","text":"Ok.  Anything  else?"},{"speaker":"B","text":"Yeah, try not to kick or hit the computer!"},{"speaker":"A","text":"Um yeah… Sorry about that."}],"vocabData":{"key":[{"word":"freeze","type":"principle verb, present simple","definition":"to stop working properly"},{"word":"take a look","type":"phrase","definition":"see, revise"},{"word":"act up","type":"principle verb, present simple","definition":"not working properly"},{"word":"be right up","type":"phrase","definition":"go upstairs soon"},{"word":"pick up","type":"principle verb, present simple","definition":"get, acquire"},{"word":"browse","type":"principle verb, present simple","definition":"look, search"},{"word":"up to date","type":"phrase","definition":"having all the most recent information"}],"supplementary":[{"word":"monitor","type":"common noun, singular","definition":"computer screen"},{"word":"computer tech","type":"common noun, singular","definition":"person in a company who fixes computers"},{"word":"corrupt file","type":"common noun, singular","definition":"damaged or broken computer file that can't be opened"},{"word":"surf the net","type":"phrase","definition":"look for information on the internet"},{"word":"web browser","type":"common noun, singular","definition":"computer program that allows you to go on the internet"}]}},{"id":8,"title":"What's Your Name Again?","level":"Elementary","mp3":"/audio/englishpod_0008pb.mp3","transcript":"https://ia600408.us.archive.org/10/items/englishpod_all/englishpod_0008.html","transcriptData":[{"speaker":"A","text":"Nick!  How's it going?"},{"speaker":"B","text":"Oh,  hey..."},{"speaker":"A","text":"What  are you doing in this neighbourhood?  Do you live around here?"},{"speaker":"B","text":"Actually,  my   office is right around the corner."},{"speaker":"A","text":"It was great to meet you last week at the conference.  I really enjoyed our conversation  about  foreign investment."},{"speaker":"B","text":"Yeah, yeah,  it  was really interesting.  You know, I'm in a bit of a hurry,   but here's my card.  We should definitely meet up again and continue our discussion."},{"speaker":"A","text":"Sure,  you still have my contact details,  right ?"},{"speaker":"B","text":"You know what,  this is really embarrassing,  but your name has just  slipped my mind.   Can you remind me?"},{"speaker":"A","text":"Sure,  my name is Ana Ferris.  Don't worry about it; it  happens to me all the time.  I'm terrible with names too."}],"vocabData":{"key":[{"word":"how's it going","type":"phrase","definition":"how are you"},{"word":"around here","type":"phrase","definition":"close to here"},{"word":"around the corner","type":"phrase","definition":"on the next street"},{"word":"in bit of a hurry","type":"phrase","definition":"moving quickly, having no time"},{"word":"contact details","type":"phrase","definition":"information required to contact someone, i.e. telephone number, email address"},{"word":"slip my mind","type":"phrase","definition":"been forgotten"},{"word":"terrible with","type":"phrase","definition":"bad at"}],"supplementary":[{"word":"around there","type":"phrase","definition":"close to there"},{"word":"to recognize","type":"principle verb, imperative","definition":"to know and remember someone or something you have seen before"},{"word":"acquaintance","type":"common noun, singular","definition":"someone you know, but who is not a close friend"},{"word":"in a rush","type":"phrase","definition":"move quickly, not have much time"},{"word":"forgetful","type":"Adjective","definition":"forgetting things easily"}]}},{"id":9,"title":"Silence Please!","level":"Elementary","mp3":"/audio/englishpod_0009pb.mp3","transcript":"https://ia600408.us.archive.org/10/items/englishpod_all/englishpod_0009.html","transcriptData":[{"speaker":"A","text":"Those   people in front of us are making so much noise.  It’s so inconsiderate!"},{"speaker":"B","text":"Don’t worry   about it;  it's  not such a big deal."},{"speaker":"A","text":"Oh...   I  can’t  hear a thing!   Excuse me,    can you keep it down?"},{"speaker":"A","text":"Someone’s phone is ringing!"},{"speaker":"B","text":"Honey, I think it’s your phone.  Did you forget to switch it off?"},{"speaker":"A","text":"Oh, no!   You’re right.  That’s so embarrassing!"}],"vocabData":{"key":[{"word":"inconsiderate","type":"Adjective","definition":"not thinking of the feelings of other people; not thoughtful"},{"word":"not such a big deal","type":"phrase","definition":"not a big problem"},{"word":"can't hear a thing","type":"phrase","definition":"unable to hear"},{"word":"keep it down","type":"phrase","definition":"be quiet"},{"word":"switch off","type":"phrase","definition":"turn off"},{"word":"do you mind","type":"phrase","definition":"could you please"}],"supplementary":[{"word":"noisy","type":"Adjective","definition":"very loud"},{"word":"rude","type":"Adjective","definition":"impolite"},{"word":"to disturb","type":"principle verb, infinitive","definition":"to interrupt, bother, or upset"},{"word":"to interrupt","type":"principle verb, infinitive","definition":"to speak or make noise when someone else is speaking"},{"word":"to whisper","type":"principle verb, infinitive","definition":"to speak very softly"}]}},{"id":10,"title":"Driving Sales","level":"Intermediate","mp3":"/audio/englishpod_0010pb.mp3","transcript":"https://ia600408.us.archive.org/10/items/englishpod_all/englishpod_0010.html","transcriptData":[{"speaker":"A","text":"All  right,   people. We're  holding this meeting today because we've got to do something about our sales, and we need to do it NOW! I want concrete solutions. How do you intend to drive sales...  Roger?"},{"speaker":"B","text":"Well, in fact,  we're the most expensive in the market,   so maybe we need to lower our prices to match the competitors?"},{"speaker":"A","text":"Lower our prices? Not very creative. It'll never fly  with Swan. What kind of thinking is that? Geez. Anybody else have a better plan? Natalie?"},{"speaker":"A","text":"What? That's the same thing. Bad idea. Really bad idea. Dammit people come on! Think!  The CEO will be here any minute."},{"speaker":"A","text":"Yeah, exactly. Just what I was thinking!  In fact,   that's a brilliant idea!  I'm glad we thought of that. Very creative."}],"vocabData":{"key":[{"word":"concrete solutions","type":"phrase","definition":"a real or specific solution to a problem"},{"word":"drive sales","type":"Preposition","definition":"increase sales"},{"word":"in the market","type":"phrase","definition":"in the industry"},{"word":"to match the competitors","type":"principle verb, infinitive","definition":"be the as good as or better than others companies in the same industry"},{"word":"will never fly","type":"phrase","definition":"will not work, will not be approved"},{"word":"promotion","type":"phrase","definition":"something done to make people aware of a product"},{"word":"be here any minute","type":"phrase","definition":"will arrive very soon"},{"word":"to consider","type":"principle verb, infinitive","definition":"thinking about"},{"word":"brilliant","type":"Adjective","definition":"excellent"}],"supplementary":[{"word":"competitive","type":"Adjective","definition":"as good as or better than others of the same type"},{"word":"win sales","type":"phrase","definition":"make sales sucessfully"},{"word":"sales strategy","type":"phrase","definition":"plans for a company's sales activities"},{"word":"sales cycle","type":"phrase","definition":"the process a customer goes though when deciding to buy a product"},{"word":"to outsell","type":"principle verb, infinitive","definition":"to sell more than others"},{"word":"customer loyalty","type":"phrase","definition":"when a customer buys the same brand over and over"}]}},{"id":11,"title":"New Guy In Town I","level":"Upper-Intermediate","mp3":"/audio/englishpod_0011pb.mp3","transcript":"https://ia600408.us.archive.org/10/items/englishpod_all/englishpod_0011.html","transcriptData":[{"speaker":"A","text":"Oh,   I don't know if you heard,  but someone moved into that old house down the road."},{"speaker":"B","text":"Yeah,  I know.  I met the owner of the house yesterday as he was moving in.  His name is Armand."},{"speaker":"A","text":"Really?  What's he like?  You have to  fill me in."},{"speaker":"B","text":"Actually,  he's a bit strange.  I don't know... I've got  a bad feeling about him."},{"speaker":"A","text":"Really?  Why?"},{"speaker":"B","text":"Well,  yesterday I brought over a housewarming gift,  but Armand started acting really weird,  and then he practically kicked me out!  I tried to,  sort of, peek into his house,  but everything was so dark  inside that I couldn't really get a good look."},{"speaker":"A","text":"Well,  you'll never guess what I saw this morning.  A delivery truck pulled into his driveway,  and it dropped off a long,  rectangular box.  It almost looked like a coffin!"},{"speaker":"B","text":"You see!  Why  would he..."},{"speaker":"B","text":"Ah,  Armand!  You scared the heck out of me!  This is my friend Doris."}],"vocabData":{"key":[{"word":"I don't know if you heard","type":"phrase","definition":"phrase used to introduce a piece of information"},{"word":"fill me in","type":"phrase","definition":"tell me about it"},{"word":"a bad feeling","type":"phrase","definition":"a sense or feeling that something bad is going to happen"},{"word":"weird","type":"Adjective","definition":"strange, unusual"},{"word":"kick out","type":"principle verb, past simple","definition":"make or force someone leave when they don't want to"},{"word":"creep me out","type":"phrase","definition":"make me feel uncomfortable and a bit scared"},{"word":"you'll never guess","type":"phrase","definition":"phrase used to introduce a piece of news"},{"word":"scare the heck out of me","type":"phrase","definition":"cause someone to feel a lot of fear"}],"supplementary":[{"word":"bizarre","type":"Adjective","definition":"strange or unusual"},{"word":"creepy","type":"Adjective","definition":"strange or scary, causing people to feel nervous and afraid"},{"word":"vampire","type":"common noun, singular","definition":"a dead person who drinks the blood of living people"},{"word":"have you heard","type":"phrase","definition":"phrase used to introduce a piece of gossip"},{"word":"guess what","type":"phrase","definition":"phrase used to introduce an interesting or surprising piece of information"}]}},{"id":12,"title":"Cleaning the House","level":"Intermediate","mp3":"/audio/englishpod_0012pb.mp3","transcript":"https://ia600408.us.archive.org/10/items/englishpod_all/englishpod_0012.html","transcriptData":[{"speaker":"A","text":"Honey, the  house is such a mess!  I need you to help me tidy up a bit.  My boss and her husband are coming over for dinner and the house needs to be  spotless!"},{"speaker":"B","text":"I'm  in the middle of something right now. I'll be   there in a second."},{"speaker":"A","text":"This can't wait!  I need your help now!"},{"speaker":"B","text":"Alright, alright.  I'm coming."},{"speaker":"A","text":"Ok, here's a list of chores we need to get   done.  I'll do the dishes and get all the groceries for tonight.  You can sweep and mop the floors.  Oh, and the furniture needs to be dusted."},{"speaker":"B","text":"You know what, I have to  pick something up at the mall, so why don't you clean the floors and I’ll go to the supermarket and get all the groceries."},{"speaker":"A","text":"Sure that's fine.  Here is the list of all the things you need to get. Don’t  forget anything! And can you pick up a bottle of wine on your way home?"},{"speaker":"B","text":"Hey, honey I'm  back. Wow, the house looks really good!"},{"speaker":"A","text":"Great!  Can you set the table?"},{"speaker":"B","text":"Just a sec I'm   just gonna vacuum this rug real fast"},{"speaker":"A","text":"Wait! Don't turn it on..."}],"vocabData":{"key":[{"word":"such a mess","type":"phrase","definition":"very dirty, disordered"},{"word":"tidy up","type":"principle verb, present simple","definition":"put things in place, organize"},{"word":"spotless","type":"Adjective","definition":"perfectly clean"},{"word":"in the middle of something","type":"phrase","definition":"busy doing something at the moment"},{"word":"chores","type":"common noun, plural","definition":"common house tasks"},{"word":"groceries","type":"common noun, plural","definition":"food that you buy at a store"}],"supplementary":[{"word":"immaculate","type":"Adjective","definition":"perfectly clean, having no dirt at all"},{"word":"do the laundry","type":"phrase","definition":"wash the dirty clothes"},{"word":"dish detergent","type":"phrase","definition":"soap used to wash the plates, knives, forks, bowls, etc."},{"word":"trash","type":"common noun, non-variable","definition":"garbage (AmE)"},{"word":"rubbish","type":"common noun, non-variable","definition":"garbage (BrE)"}]}},{"id":13,"title":"Out Of Control Spending","level":"Intermediate","mp3":"/audio/englishpod_0013pb.mp3","transcript":"https://ia600408.us.archive.org/10/items/englishpod_all/englishpod_0013.html","transcriptData":[{"speaker":"A","text":"OK, so now the last point on our agenda.  Jill, let's go over the profit and loss statement."},{"speaker":"B","text":"Great.  Well,    the main issue here, as you can see, is that our expenses are through the roof."},{"speaker":"A","text":"Let's see... These numbers are off the charts!  What's going on here!"},{"speaker":"B","text":"Well, um, sir,  the company expenditures on entertainment and travel are out of control.  Look at these bills for example. Just this month we've paid over twenty thousand dollars for hotel charges!"},{"speaker":"A","text":"OK,  thank you. I'll look into it."},{"speaker":"B","text":"The list goes on and on.  Here, this is a bill for five thousand dollars for spa treatments!"},{"speaker":"A","text":"Thank you; that will be all. I'll   take care of it."},{"speaker":"B","text":"Look at  this one sir, eight thousand dollars were spent in one night at a place called \"Wild Things\"?!"},{"speaker":"A","text":"OK,  I get it!!  Thank you for your very thorough analysis!"}],"vocabData":{"key":[{"word":"go over","type":"phrase","definition":"revise, check"},{"word":"through the roof","type":"phrase","definition":"suddenly very high"},{"word":"off the charts","type":"phrase","definition":"very high"},{"word":"expenditures","type":"common noun, plural","definition":"amount of money spent on something"},{"word":"out of control","type":"phrase","definition":"not in control"},{"word":"look into","type":"principle verb, Will Future","definition":"investigate, try to get information about something"},{"word":"go on and on","type":"phrase","definition":"continues"},{"word":"take care of","type":"principle verb, Will Future","definition":"do something about"}],"supplementary":[{"word":"revenue","type":"common noun, non-variable","definition":"amount of money paid to a business"},{"word":"cash flow","type":"common noun, non-variable","definition":"the movement of money into and out of a business"},{"word":"the bottom line","type":"phrase","definition":"the net profit;  the amount of money a business makes after expenses"},{"word":"finances","type":"common noun, non-variable","definition":"the amount of money you have and how well it is organized"},{"word":"income","type":"common noun, non-variable","definition":"the amount of money a person or a company makes"},{"word":"assets","type":"common noun, plural","definition":"things of value that are owned by a company"}]}},{"id":14,"title":"I'm in Debt","level":"Elementary","mp3":"/audio/englishpod_0014pb.mp3","transcript":"https://ia600408.us.archive.org/10/items/englishpod_all/englishpod_0014.html","transcriptData":[{"speaker":"A","text":"Hello, I’m here to see Mr. Corleone."},{"speaker":"B","text":"Right this way, sir."},{"speaker":"B","text":"Mr. Corlone, I’m really sorry to trouble you, but I need your help."},{"speaker":"B","text":"Well, sir, you see, this recession has hit me pretty hard; I lost my job and I’m in a lot of debt."},{"speaker":"B","text":"Yeah, you know, I’ve got credit card   bills, car payments, I’ve got to pay my mortgage; and on top of all that,  I have to pay my son’s college tuition."},{"speaker":"B","text":"Well, I just thought maybe you could help me out."}],"vocabData":{"key":[{"word":"trouble","type":"principle verb, present simple","definition":"disturb or bother"},{"word":"recession","type":"common noun, singular","definition":"a period of time when the economy of a country is bad"},{"word":"hit me pretty hard","type":"phrase","definition":"had a strong, negative affect on me"},{"word":"debt","type":"common noun, non-variable","definition":"the amount of money you owe to a person, bank, company, etc."},{"word":"on top of all that","type":"phrase","definition":"besides that, in addition to"},{"word":"broke","type":"Adjective","definition":"not have any money"}],"supplementary":[{"word":"borrow","type":"principle verb, present simple","definition":"take and use some money, and return the equal amount of money at a later time"},{"word":"pay back","type":"phrase","definition":"return the money you borrowed"},{"word":"a loan","type":"common noun, singular","definition":"amount of money borrowed"},{"word":"take out a loan","type":"phrase","definition":"borrow money"},{"word":"unemployed","type":"Adjective","definition":"having no job"},{"word":"downturn","type":"common noun, singular","definition":"a time when the economy is worse than usual"}]}},{"id":15,"title":"I'm Sorry I Love You I","level":"Intermediate","mp3":"/audio/englishpod_0015pb.mp3","transcript":"https://ia600408.us.archive.org/10/items/englishpod_all/englishpod_0015.html","transcriptData":[{"speaker":"A","text":"Whoa,  whoa, what's going on?  Watch out!"},{"speaker":"B","text":"Hey,  watch where you're going!"},{"speaker":"A","text":"Oh,  no!  I'm so sorry!  Are you all right?"},{"speaker":"B","text":"Oh...I don't know."},{"speaker":"A","text":"I feel terrible,  I really  didn't mean to knock you over.  My tire,  just exploded,  and  I lost  control  of my bike.  Really,  it was  an accident.   Please accept my apologies."},{"speaker":"B","text":"Just let  me try to stand up."},{"speaker":"A","text":"Are you okay?"},{"speaker":"B","text":"Oh,  wait a second,  you seem really familiar,  I think I know you  from   somewhere."},{"speaker":"A","text":"Yeah,  I think we have met  somewhere before.  That's right!  We met at  Aaron's  place last weekend!  What a coincidence!   But anyway,  I'm glad to see  that you're not too badly hurt,  and I should probably  get going.  I have a nine o'clock meeting."},{"speaker":"B","text":"Ouch! My ankle!  I think it's broken!  You can't just  leave me like this!  Are you calling an ambulance?"},{"speaker":"A","text":"Nope,  I'm canceling my appointment  so that I can stay here with you."}],"vocabData":{"key":[{"word":"feel terrible","type":"phrase","definition":"sorry"},{"word":"mean","type":"principle verb, present simple","definition":"intend, plan"},{"word":"explode","type":"principle verb, past simple","definition":"suddenly break apart"},{"word":"accident","type":"common noun, singular","definition":"an event that is not planed or intended"},{"word":"familiar","type":"Adjective","definition":"probably known, but not clearly remembered"},{"word":"place","type":"common noun, singular","definition":"house"},{"word":"coincidence","type":"common noun, singular","definition":"situation when two or more things happen at the same time in an unplanned way"},{"word":"knock over","type":"principle verb, infinitive","definition":"make something fall over"}],"supplementary":[{"word":"regret","type":"principle verb, present simple","definition":"feel sad or sorry about something"},{"word":"apologetic","type":"Adjective","definition":"feeling or showing regret, showing an apology"},{"word":"feel guilty","type":"phrase","definition":"feel responsible for doing something bad"},{"word":"unfamiliar","type":"Adjective","definition":"not familiar"},{"word":"love at first sight","type":"phrase","definition":"falling in love the first time you see someone"}]}},{"id":16,"title":"Turn left here!","level":"Elementary","mp3":"/audio/englishpod_0016pb.mp3","transcript":"https://ia600408.us.archive.org/10/items/englishpod_all/englishpod_0016.html","transcriptData":[{"speaker":"A","text":"Hurry up,  get in!"},{"speaker":"B","text":"I'm in ,  let's go!"},{"speaker":"A","text":"OK,  make a left here...no wait,   I meant make a right.  Come on speed up!"},{"speaker":"B","text":"Geeze!  What's the rush?"},{"speaker":"A","text":"Don't worry about it,  just  drive.  Oh, no,  the light  is about to change... step on it!"},{"speaker":"B","text":"Are you nuts?  I'm not going to run a red light!"},{"speaker":"A","text":"Whatever.  Just turn  right here.  The freeway   will be packed at this hour.... let's take a side street.  Go on!  Get out of our way!   Move,  move!"},{"speaker":"B","text":"What's your problem?  Geez.  Having a fit is not going to help!"},{"speaker":"A","text":"Here,  I know a short cut... just go down here and we'll cut through Ashburn Heights.  Let's go,    let's go!  Watch out for that lady!"},{"speaker":"B","text":"I'm going as fast as I can!"},{"speaker":"A","text":"Yes!  We made it.  5:58,  just before the library closes."},{"speaker":"B","text":"You're such a geek!"}],"vocabData":{"key":[{"word":"hurry up","type":"phrase","definition":"move faster"},{"word":"make a left","type":"phrase","definition":"turn left"},{"word":"step on it","type":"phrase","definition":"drive a lot faster"},{"word":"nuts","type":"Adjective","definition":"crazy"},{"word":"speed up","type":"verb","definition":"go faster"},{"word":"have a fit","type":"phrase","definition":"get very angry"},{"word":"cut through","type":"phrase","definition":"quickly go past or though something that blocks you"},{"word":"packed","type":"Adjective","definition":"full, having lots of cars"}],"supplementary":[{"word":"road rage","type":"phrase","definition":"be very angry at other drivers while driving"},{"word":"back street","type":"phrase","definition":"small street that is away from the main streets"},{"word":"u-turn","type":"phrase","definition":"a 180 degree turn in the car"},{"word":"to back up","type":"principle verb, infinitive","definition":"to move backwards, the opposite of go forwards"},{"word":"to pass","type":"principle verb, infinitive","definition":"to drive past another car on the road to pass a car on the road"},{"word":"to park","type":"principle verb, infinitive","definition":"to stop the car and turn off the engine"}]}},{"id":17,"title":"Here Comes the Bride","level":"Elementary","mp3":"/audio/englishpod_0017pb.mp3","transcript":"https://ia600408.us.archive.org/10/items/englishpod_all/englishpod_0017.html","transcriptData":[{"speaker":"A","text":"I can't believe that Anthony is finally getting married!"},{"speaker":"B","text":"Yeah,  well,  it's about time.  He's been living with his parents for forty years!"},{"speaker":"A","text":"Don't be mean.  Look,   here come the bridesmaids!  Their dresses look beautiful!"},{"speaker":"B","text":"Who are those kids walking down the aisle?"},{"speaker":"A","text":"That's the flower girl and the ring bearer. I'm pretty sure they're the groom's niece and nephew.  Oh, they look so cute!"},{"speaker":"B","text":"I just hope the priest makes it quick.  I'm starving.  I hope the food's good at the reception."},{"speaker":"A","text":"That’s all you ever think about, food!  Oh, I think the bride's coming now!  She looks gorgeous. Wait, what's she doing? Where's she going?"},{"speaker":"B","text":"Oh, great! Does this mean that the reception is canceled?"}],"vocabData":{"key":[{"word":"married","type":"Adjective","definition":"joined in marriage"},{"word":"it's about time","type":"phrase","definition":"finally"},{"word":"bridesmaid","type":"common noun, plural","definition":"female friends or relatives who helps the bride at a wedding"},{"word":"aisle","type":"common noun, singular","definition":"a passage between sections of seats"},{"word":"flower girl","type":"common noun, singular","definition":"a girl at a wedding that throws flower petals down the aisle"},{"word":"ring bearer","type":"common noun, singular","definition":"a boy at a wedding that holds the wedding rings"},{"word":"reception","type":"common noun, singular","definition":"party held to celebrate something or to welcome people"},{"word":"groom","type":"common noun, singular","definition":"a man who is about to be married"},{"word":"bride","type":"common noun, singular","definition":"female who is about to get married"},{"word":"gorgeous","type":"Adjective","definition":"very beautiful or attractive"}],"supplementary":[{"word":"groomsman","type":"common noun, singular","definition":"male friend or relative who helps the groom on the wedding day"},{"word":"maid of honor","type":"common noun, singular","definition":"female fiend or relative who acts as a witness at the wedding"},{"word":"best man","type":"common noun, singular","definition":"male friend or relative who acts as a witness at the wedding"},{"word":"ushers","type":"common noun, plural","definition":"men who help guest find their seats"}]}},{"id":18,"title":"Protest!","level":"Upper-Intermediate","mp3":"/audio/englishpod_0018pb.mp3","transcript":"https://ia600408.us.archive.org/10/items/englishpod_all/englishpod_0018.html","transcriptData":[{"speaker":"A","text":"This is Action 5 News reporter Sarah O'Connell  reporting  live from Washington,   D. C. where a  protest    has broken out.  Thousands of  angry citizens are protesting  against the  proposed bailout of  the auto manufacturing industry!   Sir,  sir,  Sar"},{"speaker":"B","text":"Yeah,  yeah,  we  are  here  because  we  feel  this  is  an  injustice!   The  financial irresponsibility of  big business has to stop!    We're  here   to show  the   government that  we  don't   like  the  way  that  they're  spending    our  tax dolla"},{"speaker":"A","text":"Sir,  but  what exactly   is making  everyone so  angry?"},{"speaker":"B","text":"It's  an  absolute  outrage,   Sarah,  the  US government  wants to give twenty-five billion  dollars of  taxpayers' money to  the  auto industry.  These are companies that  have been mismanaged and  are  now nearly bankrupt."},{"speaker":"A","text":"I see.   But,  many supporters of  the bailout argue  that  it  could  help save  the  jobs of  millions of  hardworking Americans."},{"speaker":"B","text":"That may be  true,  and I for one don't  want  to see  anyone  lose their  job,  but  how can these  CEOs ask  for  a  bailout when  they're  making  millions of  dollars?    And  then they have the nerve  to fly  to  Washington   in  private  jets!  This"},{"speaker":"A","text":"Good point.   This  is  Sarah O'Connell  reporting  live  from  Washington D. C.,  back to  you,   Tom."}],"vocabData":{"key":[{"word":"protest","type":"common noun, singular","definition":"an event where people gather together to show disapproval of something"},{"word":"break out","type":"principle verb, present perfect","definition":"start suddenly"},{"word":"bailout","type":"common noun, singular","definition":"the act of saving a company form money problems"},{"word":"injustice","type":"common noun, singular","definition":"an unfair situation"},{"word":"outrage","type":"common noun, singular","definition":"something that is morally wrong"},{"word":"mismanage","type":"principle verb, present perfect","definition":"manage badly"},{"word":"have the nerve","type":"phrase","definition":"dare to"}],"supplementary":[{"word":"placard","type":"common noun, singular","definition":"sign  people hold at protests, usually a piece of cardboard on a stick"},{"word":"rally","type":"common noun, singular","definition":"public meeting in support or opposition of something"},{"word":"demonstration","type":"common noun, singular","definition":"event where people gather together to show that they support or oppose something"},{"word":"demonstrators","type":"common noun, plural","definition":"people who go to a demonstration or protest"},{"word":"opponent","type":"common noun, singular","definition":"a person who opposes something or someone"},{"word":"proponent","type":"common noun, singular","definition":"person who supports someone or something"}]}},{"id":19,"title":"Christmas Chronicles I","level":"Intermediate","mp3":"/audio/englishpod_0019pb.mp3","transcript":"https://ia600408.us.archive.org/10/items/englishpod_all/englishpod_0019.html","transcriptData":[{"speaker":"A","text":"I hate working on Christmas Eve!  Whoa!  Get a load of  this guy!  Come in  central,  I think we've got ourselves a situation here."},{"speaker":"B","text":"License and registration  please.   Have you been drinking  tonight,  sir?"},{"speaker":"A","text":"I had one or two glasses of eggnog,  but  nothing else."},{"speaker":"B","text":"Step out  of the  vehicle,  please.  Sir,  what do you have in the  back?"},{"speaker":"A","text":"Just a few Christmas  gifts,  'tis the season,  after all!"},{"speaker":"B","text":"Don't take  that tone  with me.  Do you  have an invoice  for these items?"},{"speaker":"A","text":"Umm...no...I make these in my workshop  in the  North Pole!"},{"speaker":"B","text":"You are under arrest,  sir.   You have the right to remain  silent.  You better not pout,  you better not cry.   Anything  you say  can and will be used against you.   You have the right to an attorney;  if  you cannot afford one,  the state  will appoint"},{"speaker":"A","text":"You  can't  take me  to  jail!  What about  my sleigh?   It's Christmas Eve!  I have presents to deliver!  Rudolph!   Prancer!   Dancer!   Get help!"}],"vocabData":{"key":[{"word":"get a load of","type":"phrase","definition":"look at (informal)"},{"word":"license","type":"common noun, singular","definition":"official document that shows you have permission to drive a car"},{"word":"eggnog","type":"common noun, non-variable","definition":"traditional Christmas drink containing eggs, cream, sugar and rum"},{"word":"vehicle","type":"common noun, singular","definition":"machine use to carry people, i.e. a car or truck"},{"word":"step out","type":"principle verb, imperative","definition":"briefly go out of a place"},{"word":"take that tone","type":"phrase","definition":"speak in that tone of voice"},{"word":"invoice","type":"common noun, singular","definition":"official document that shows you have paid for something you bought"},{"word":"arrest","type":"phrase","definition":"the act of legally taking someone to jail"},{"word":"pout","type":"principle verb, present simple","definition":"push out your lips to show that you are unhappy"},{"word":"sleigh","type":"common noun, singular","definition":"large, open vehicle that is pulled by horses over snow or ice"}],"supplementary":[{"word":"handcuffs","type":"common noun, plural","definition":"a set of two metal rings that are used to join a persons hands together, esp. by police"},{"word":"prison","type":"common noun, singular","definition":"a building where criminals are kept as punishment"},{"word":"read someone's rights","type":"phrase","definition":"tell someone their legal rights, usually when they are arrested"},{"word":"DUI","type":"phrase","definition":"driving under the influence of alcohol or drugs"},{"word":"speeding ticket","type":"common noun, singular","definition":"a piece of paper the police give if you are diving too fast"}]}},{"id":20,"title":"I Can See Clearly Now","level":"Elementary","mp3":"/audio/englishpod_0020pb.mp3","transcript":"https://ia600408.us.archive.org/10/items/englishpod_all/englishpod_0020.html","transcriptData":[{"speaker":"A","text":"Hello,  Arthur. What seems to be the problem?"},{"speaker":"B","text":"Hey doc.   Well, I think I   might   need glasses.   I'm  getting headaches, and I really struggle to see   things that are far away.   But I   have always had 20/20 vision."},{"speaker":"A","text":"Sounds like you may be  far-sighted.   OK,   then,     cover your left   eye   and read  the chart  in front of  you."},{"speaker":"B","text":"Mmm… X, E, R, 3, a question mark,   and I can't quite make out  the other   symbol   but   I  think  it's    the peace sign."},{"speaker":"A","text":"Wow,  Arthur! You're as blind as a bat!"},{"speaker":"B","text":"Yeah,   I know, my vision is really blurry at times."},{"speaker":"A","text":"Ok, then,  head on over to the other room  and pick out  some frames while I   fill out your prescription."},{"speaker":"B","text":"Thanks,  doc!"},{"speaker":"A","text":"Arthur, that's the bathroom."}],"vocabData":{"key":[{"word":"seem to be","type":"phrase","definition":"appears or is"},{"word":"struggle","type":"verb","definition":"try very hard"},{"word":"20/20 vision","type":"phrase","definition":"perfect eyesight"},{"word":"make out","type":"verb","definition":"see"},{"word":"far sighted","type":"phrase","definition":"see far-away things more clearly than things nearby"},{"word":"as blind as a bat","type":"phrase","definition":"have really poor vision"},{"word":"vision","type":"common noun, singular","definition":"ability to see things"},{"word":"blurry","type":"Adjective","definition":"not clear"},{"word":"pick out","type":"phrase","definition":"choose"},{"word":"prescription","type":"common noun, singular","definition":"a message written by a doctor that tells people what medicine to take"}],"supplementary":[{"word":"check-up","type":"common noun, singular","definition":"regular examination by a doctor to make sure a person is healthy"},{"word":"short sighted","type":"Adjective","definition":"unable to see things far away (AmE)"},{"word":"nearsighted","type":"Adjective","definition":"unable to see things far away (BrE)"},{"word":"contacts","type":"common noun, plural","definition":"lenses you wear directly on your eyes to help you see"},{"word":"optometrist","type":"common noun, singular","definition":"eye doctor"},{"word":"prescribe","type":"verb","definition":"officially tell someone to use a medicine or treatment"},{"word":"lenses","type":"common noun, plural","definition":"clear, curved piece of glass that helps people see better"}]}},{"id":21,"title":"What Do You Do?","level":"Intermediate","mp3":"/audio/englishpod_0021pb.mp3","transcript":"https://ia600408.us.archive.org/10/items/englishpod_all/englishpod_0021.html","transcriptData":[{"speaker":"A","text":"Oh,  look,  there's  Veronica and  her  boyfriend.    She's   always  going on about him  at  the  office.  Oh,   great,    they  saw  us.    They're coming this way."},{"speaker":"B","text":"Oh, man..."},{"speaker":"A","text":"Nice to meet   you.   This is   my husband,  Arthur."},{"speaker":"B","text":"Hey,   how's it going?"},{"speaker":"A","text":"Veronica   talks  about you  all the time.   I  guess  you  must  be  pretty  busy   at  work."},{"speaker":"B","text":"Wow,   yeah... that  sounds exciting."},{"speaker":"B","text":"Oh,   I'm  a  Top Gun pilot!"}],"vocabData":{"key":[{"word":"go on about","type":"phrase","definition":"talk about something a lot"},{"word":"Fortune 500","type":"phrase","definition":"top 500 companies, rated by revenue"},{"word":"implement","type":"principle verb, present progressive","definition":"begin to do something, put into action"},{"word":"responsible","type":"Adjective","definition":"have the duty or job of taking care of something"},{"word":"nationwide","type":"Adjective","definition":"in the whole country"},{"word":"look after","type":"principle verb, infinitive","definition":"take care of"},{"word":"oversee","type":"principle verb, present simple","definition":"watch and direct to make sure a job is done correctly"},{"word":"quality control","type":"common noun, non-variable","definition":"checking goods to make sure that they are good enough to sell"},{"word":"under me","type":"phrase","definition":"working for me"}],"supplementary":[{"word":"develop relationships","type":"phrase","definition":"make good relationships"},{"word":"handle complaints","type":"phrase","definition":"look after or take care of complaints"},{"word":"coordinate","type":"verb","definition":"make arrangements or plans with a group of people"},{"word":"monitor","type":"verb","definition":"watch and check"},{"word":"supervise","type":"verb","definition":"be in charge of something , watch and direct"},{"word":"ensure","type":"verb","definition":"make sure"}]}},{"id":22,"title":"Christmas Chronicles II","level":"Intermediate","mp3":"/audio/englishpod_0022pb.mp3","transcript":"https://ia600408.us.archive.org/10/items/englishpod_all/englishpod_0022.html","transcriptData":[{"speaker":"A","text":"Really, gentlemen,   you can't take me to jail!   Don't  you know who I am?  Kris Kringle,  you  know,    Papa Noel,   Pere Noel,     Babbo Natale,  sheng dan lao ren!"},{"speaker":"B","text":"Yeah, Yeah, we've  heard that one before, haven't  we Joe?"},{"speaker":"A","text":"It's Christmas Eve and I have all these presents to deliver!  Where is your Christmas spirit?   What will happen when all the children wake up tomorrow and don't find  any   gifts in their stockings?"},{"speaker":"B","text":"Sorry buddy, you  were parked   in  a   no-parking zone,    you were speeding,  and you have no ID!"},{"speaker":"A","text":"What! This is unbelievable! What's this world coming  to? Christmas is ruined!"}],"vocabData":{"key":[{"word":"take someone to jail","type":"phrase","definition":"take someone who is arrested to a place where other criminals are kept"},{"word":"book","type":"verb","definition":"arrest (informal)"},{"word":"claim","type":"verb","definition":"say something is true when some people say it may not be true"},{"word":"stocking","type":"common noun, singular","definition":"large sock; traditionally, Christmas gifts are put inside on the Christmas Eve"},{"word":"speed","type":"verb","definition":"drive faster than legal speed limit"},{"word":"impound","type":"verb","definition":"held by the police"},{"word":"ruined","type":"Adjective","definition":"damaged or destroyed"},{"word":"reindeer","type":"common noun, non-variable","definition":"large deer that lives in the north part of the globe"},{"word":"elf","type":"common noun, singular","definition":"small creature (in stories) that have pointed ears and magic powers"},{"word":"candy cane","type":"common noun, plural","definition":"a stick-shaped candy with red and white curves on the top"},{"word":"under heavy","type":"phrase","definition":"being attacked or hurt"},{"word":"backup","type":"common noun, non-variable","definition":"help"}],"supplementary":[{"word":"charge","type":"verb","definition":"formally accuse someone of a crime"},{"word":"pull someone over","type":"phrase","definition":"make a moving car move to the side of the road"},{"word":"ambush","type":"common noun, singular","definition":"a sudden attack"},{"word":"stocking stuffer","type":"common noun, singular","definition":"small gifts that are put in the Christmas stocking"},{"word":"Christmas ornament","type":"common noun, singular","definition":"decoration that is used to make the Christmas tree beautiful"}]}},{"id":23,"title":"Making an Appointment","level":"Elementary","mp3":"/audio/englishpod_0023pb.mp3","transcript":"https://ia600408.us.archive.org/10/items/englishpod_all/englishpod_0023.html","transcriptData":[{"speaker":"A","text":"Hello, Fairbrook Consulting,   how may I help you?"},{"speaker":"B","text":"Yes,  this  is Julianne Horton,   and  I'm  calling to arrange  an  appointment  with Ms.  McNealy."},{"speaker":"A","text":"Certainly,     what  day   were  you   thinking  of?"},{"speaker":"B","text":"How's  Thursday?   Does  she  have  any  time  available  then?"},{"speaker":"A","text":"Um... let  me  double check...unfortunately,  she's  booked solid on Thursday,   how does  next Monday work for you?"},{"speaker":"B","text":"Actually,   I've got  something  scheduled  on  Monday.   Can she  do Tuesday?"},{"speaker":"A","text":"Sure,   Tuesday's  perfect.   May  I  ask  where you're   calling from?"},{"speaker":"B","text":"Sure,   Merton Financial Advisors."},{"speaker":"A","text":"Oh, actually,   Tuesday's no good.  Sorry 'bout that."}],"vocabData":{"key":[{"word":"appointment","type":"common noun, singular","definition":"an agreement to meet someone at a particular time"},{"word":"available","type":"Adjective","definition":"not busy, willing to talk to someone"},{"word":"double check","type":"phrase","definition":"check again"},{"word":"booked solid","type":"phrase","definition":"having no available place or time"},{"word":"work for you","type":"phrase","definition":"be okay with you"},{"word":"scheduled","type":"verb","definition":"planned at a specific time"},{"word":"no good","type":"phrase","definition":"not okay"}],"supplementary":[{"word":"fully booked","type":"phrase","definition":"booked solid; having no space or time available"},{"word":"fit you in","type":"phrase","definition":"find time to see someone in a busy schedule"},{"word":"unavailable","type":"Adjective","definition":"not available"},{"word":"occupied","type":"Adjective","definition":"busy doing something"}]}},{"id":24,"title":"Where Should We Eat?","level":"Elementary","mp3":"/audio/englishpod_0024pb.mp3","transcript":"https://ia600408.us.archive.org/10/items/englishpod_all/englishpod_0024.html","transcriptData":[{"speaker":"A","text":"Do you two have any plans for the evening?"},{"speaker":"B","text":"We were thinking of  checking out  a restaurant in the neigbourhood.  Do you have any suggestions?"},{"speaker":"A","text":"I know this really nice Italian place.  The food is fantastic, and the décor is beautiful.  I'd recommend giving it a try."},{"speaker":"A","text":"In that case,  I know a great little bistro.  They make a really tasty seafood platter;   the fish is outstanding."},{"speaker":"B","text":"It sounds fantastic, but I’m allergic to seafood, so..."},{"speaker":"A","text":"Okay, well, let me think...   Oh, I know this great little place.  It's just a hole in the wall, but they do the most amazing   sandwiches. You gotta give them a try."}],"vocabData":{"key":[{"word":"check out","type":"phrase","definition":"look at something that is attractive"},{"word":"fantastic","type":"Adjective","definition":"extremely good"},{"word":"décor","type":"common noun, singular","definition":"decoration"},{"word":"in the mood for","type":"phrase","definition":"have a desire for something or to do something"},{"word":"light","type":"Adjective","definition":"not heavy on stomach, easy to digest"},{"word":"bistro","type":"common noun, singular","definition":"small, trendy restaurant or bar"},{"word":"tasty","type":"Adjective","definition":"delicious, tasting good"},{"word":"outstanding","type":"Adjective","definition":"excellent"},{"word":"a hole in the wall","type":"phrase","definition":"a very small, usually cheap restaurant or bar"},{"word":"food poisoning","type":"phrase","definition":"becoming sick because of bacteria in food"}],"supplementary":[{"word":"yummy","type":"Adjective","definition":"tasting good"},{"word":"crazy about","type":"phrase","definition":"like very much"},{"word":"not fond of","type":"phrase","definition":"don't like (polite)"},{"word":"enjoyable","type":"Adjective","definition":"tasting good"},{"word":"yum","type":"Interjection","definition":"expression you use when you're enjoying food or drink"}]}},{"id":25,"title":"Planning For The Worst","level":"Upper-Intermediate","mp3":"/audio/englishpod_0025pb.mp3","transcript":"https://ia600408.us.archive.org/10/items/englishpod_all/englishpod_0025.html","transcriptData":[{"speaker":"A","text":"Well, right, let’s move to our next    order of business,  as many of you are aware, in recent weeks there has been   a lot of media coverage surrounding this bird flu issue.  And it's come to my attention  that our company lacks any sort of   bird flu  c"},{"speaker":"B","text":"Basically, we need to come up with   a clear plan; we need to outline specific actions that our company can take to maintain critical business functions in case a pandemic  strikes."},{"speaker":"A","text":"So, what I’d  like to do   is: first appoint someone to look after drafting our plan; Ralph, I’d like you to head up  this project."},{"speaker":"B","text":"Well, let’s see,    there are  a few    points    we need  to be thinking  about...    first, I’ll need you to analyze our numbers and figure out what kind of financial impact an outbreak might have."},{"speaker":"A","text":"You’ll also need to think about how we can avoid any of our employees getting infected; think of ways to reduce  employee-customer contact,   perhaps some IT solutions  that will allow  our people to work from home."},{"speaker":"A","text":"Exactly right. So, I’ll leave this to you,   and we’ll review the draft plan in two weeks.  Okay, so, anyone want to order  some KFC for lunch?"}],"vocabData":{"key":[{"word":"order of business","type":"phrase","definition":"an issue that must be discussed"},{"word":"bird flu","type":"common noun, non-variable","definition":"flu originated from birds that carry virus"},{"word":"contingency plan","type":"phrase","definition":"a plan that prepares for a situation where things can go wrong"},{"word":"critical","type":"Adjective","definition":"extremely important"},{"word":"pandemic","type":"common noun, singular","definition":"a situation where a disease is spread out very quickly, and affects many people in many countries"},{"word":"draft","type":"verb","definition":"make a first version of something"},{"word":"impact","type":"common noun, singular","definition":"major influence or effect on something"},{"word":"outbreak","type":"common noun, singular","definition":"a sudden start of disease affecting many people"},{"word":"head up","type":"verb","definition":"lead and be responsible for"},{"word":"forecast","type":"verb","definition":"predict what is going to happen based on available information"},{"word":"vaccine","type":"common noun, plural","definition":"medical substance that protect people or animal against diseases"}],"supplementary":[{"word":"epidemic","type":"phrase","definition":"event where a disease spreads really quickly and affects a large number of people"},{"word":"backup plan","type":"phrase","definition":"a plan made in case events go badly"},{"word":"pandemic preparedness plan","type":"phrase","definition":"a plan to prepare for the possible outbreak of a pandemic"},{"word":"transmit disease","type":"phrase","definition":"pass disease from one individual to another"},{"word":"avian influenza","type":"phrase","definition":"bird flu"},{"word":"influenza","type":"common noun, non-variable","definition":"flu; illness caused by a virus"}]}},{"id":26,"title":"New Year Resolution","level":"Elementary","mp3":"/audio/englishpod_0026pb.mp3","transcript":"https://ia600408.us.archive.org/10/items/englishpod_all/englishpod_0026.html","transcriptData":[{"speaker":"A","text":"So, did I  tell  you   about  my   New Year's resolution?   I've  decided   to go  on  a   diet."},{"speaker":"B","text":"And   you're  going  to   completely transform   your  eating habits,   right?"},{"speaker":"A","text":"Exactly!   I'm   going   to cut out  all  that  junk  I  eat;  no more  chips,   no more    soda,    no more   fried  food."},{"speaker":"B","text":"I've  heard   this  one  before."},{"speaker":"A","text":"But this  time   I'm   going  to stick to  it.   I  really  mean it!   Trust  me,    Carol,   I'm    going   to be  a  new  man   in one year's time!"},{"speaker":"B","text":"Well,    I  guess  we'll just  have  to wait and see."},{"speaker":"A","text":"Thanks,   honey,    that  was  a   great  meal.   I'm   stuffed.   Do   we  have  any  chips  left?"}],"vocabData":{"key":[{"word":"diet","type":"common noun, singular","definition":"eating plan to help you lose weight"},{"word":"transform","type":"verb","definition":"change"},{"word":"cut out","type":"verb","definition":"remove, eliminate"},{"word":"eating habits","type":"phrase","definition":"usual ways of behaving"},{"word":"stick to","type":"verb","definition":"continue doing something"},{"word":"wait and see","type":"phrase","definition":"remain in a state in which you expect something to happen, and see if it does"},{"word":"stuffed","type":"Adjective","definition":"filled with food; no longer hungry"}],"supplementary":[{"word":"calorie","type":"common noun, singular","definition":"unit of energy in food"},{"word":"vegetarian","type":"common noun, singular","definition":"person who does not eat meat"},{"word":"fast food","type":"common noun, non-variable","definition":"food that is prepared and served quickly (i.e. McDonald's, KFC, Burger King, etc.)"},{"word":"work out","type":"verb","definition":"exercise at a gym"},{"word":"lifestyle change","type":"phrase","definition":"change the way you live"}]}},{"id":27,"title":"Asking for Time Off","level":"Elementary","mp3":"/audio/englishpod_0027pb.mp3","transcript":"https://ia600408.us.archive.org/10/items/englishpod_all/englishpod_0027.html","transcriptData":[{"speaker":"A","text":"Mr.      McKenna,    do   you   have a second?    I  need  to talk  to  you about  something."},{"speaker":"B","text":"Sure,    Liv,   what can I do for you?"},{"speaker":"A","text":"Well,   I   was  just  wondering... you   see,    I  know I've  used up  all  my   vacation days   this  year,     but   my   sister  is getting  married,  and   the   wedding is  overseas,     and,    well..."},{"speaker":"B","text":"You   wanna   take some time off,    is  that  right?"},{"speaker":"A","text":"Well,     sir,    I  was  just  hoping  that  I  might  be able  to take   some  unpaid leave   this   year."},{"speaker":"B","text":"What   dates  are  you  planning on   taking off?    I'll  need at least   two   months   notice,    so  that  I  can  plan  for  your  absence."},{"speaker":"A","text":"I  was thinking of  taking off   from  September first  until   the   thirtieth. Would  you  be okay with that?"},{"speaker":"B","text":"Well,    I   guess  so."}],"vocabData":{"key":[{"word":"have a second","type":"phrase","definition":"have some time, not busy"},{"word":"use up","type":"verb","definition":"take all of something"},{"word":"overseas","type":"Adjective","definition":"in an other country, across the ocean"},{"word":"take some time off","type":"phrase","definition":"stop working for a short time"},{"word":"notice","type":"common noun, non-variable","definition":"information that tells you about something or warns about something"},{"word":"be okay with that","type":"phrase","definition":"agree"},{"word":"unpaid leave","type":"phrase","definition":"time away from work that is not paid"}],"supplementary":[{"word":"take a leave of absence","type":"phrase","definition":"stay away from work for a long period of time, without getting paid"},{"word":"sick leave","type":"common noun, non-variable","definition":"time away from work when sick"},{"word":"personal leave","type":"common noun, non-variable","definition":"time away from work for personal reasons (i.e. doctor's appointment, etc.)"},{"word":"maternity leave","type":"common noun, non-variable","definition":"time away from work for a mother to care for a new baby"},{"word":"bank holiday","type":"common noun, singular","definition":"public holiday recognized by law (BrE)"}]}},{"id":28,"title":"I'm Sorry I Love You II","level":"Intermediate","mp3":"/audio/englishpod_0028pb.mp3","transcript":"https://ia600408.us.archive.org/10/items/englishpod_all/englishpod_0028.html","transcriptData":[{"speaker":"A","text":"I'm so  relieved that  your  ankle  wasn't   broken!   I  feel  just  awful   about   this whole thing.   I  wanna   make it up to you.   Let  me  take you out to dinner  tonight.   My treat."},{"speaker":"B","text":"That sounds great!   I'd  love  to!   Here  is  my  address.   Pick me up  at eight?"},{"speaker":"A","text":"Perfect!"},{"speaker":"B","text":"Thank  you for  such a  lovely  evening!  The   food  was  amazing,    and  I  had  a  great  time."},{"speaker":"A","text":"Me  too.   You  look  so  beautiful tonight!   I wish   this  night  would  never  end.   There's     something  I  have  to tell you..."},{"speaker":"B","text":"What   is it?"},{"speaker":"A","text":"I woke up  today  thinking  this  would  be  just  like  any  other   ordinary  day,    but  I  was wrong.   A  twist of fate  brought us  together.   I  crashed  into  your   life  and  you  into  mine,   and  this  may   sound crazy,   but  I'm  falling"}],"vocabData":{"key":[{"word":"relieved","type":"Adjective","definition":"relaxed and happy that something difficult has been stopped"},{"word":"make it up","type":"verb","definition":"do something good as a balance to a bad or hurtful action"},{"word":"my treat","type":"phrase","definition":"I will pay"},{"word":"pick up","type":"verb","definition":"go somewhere in order to get someone or something"},{"word":"ordinary","type":"Adjective","definition":"normal"},{"word":"twist of fate","type":"phrase","definition":"unplanned events that has a big impact on the future"},{"word":"crash","type":"verb","definition":"hit something causing serious damage"},{"word":"fall in love","type":"phrase","definition":"start being in love"}],"supplementary":[{"word":"go on a date","type":"phrase","definition":"go to an event or activity with a boyfriend or girlfriend"},{"word":"ask you out","type":"verb","definition":"invite or ask someone to go on a date (informal)"},{"word":"take you out","type":"verb","definition":"invite someone to go and do something"},{"word":"turn down","type":"verb","definition":"decline an invitation"},{"word":"look forward to","type":"verb","definition":"an expression to show excitement about something"},{"word":"see someone","type":"verb","definition":"be in a relationship with someone"},{"word":"flirt","type":"verb","definition":"behave in a way that expresses a sexual attraction for someone"}]}},{"id":29,"title":"Investing in Emerging Markets","level":"Advanced","mp3":"/audio/englishpod_0029pb.mp3","transcript":"https://ia600408.us.archive.org/10/items/englishpod_all/englishpod_0029.html","transcriptData":[{"speaker":"A","text":"Dad,   I'd  like  to borrow   some  money."},{"speaker":"B","text":"Sure,   Johnny,   how  much  do  you  need?  Five   bucks?"},{"speaker":"A","text":"Come on,   Dad,   I need  thirty thousand.   I  wanna  get  into  the  market.  You   know,   I'm   tired  of  hearing  all  this  news  about  the  economic  downturn,  the  inevitable   recession,  people   stuffing  their  money   in  their   mattresse"},{"speaker":"B","text":"I  don't  know  about  that;   with  all  the  uncertainty  in  the  market right now,    it  would be  a  very  unwise  decision  to invest.   I  don't   know   if  you're  aware son,    but  there  has been  a lot   of  turmoil in  the  markets  recentl"},{"speaker":"A","text":"But  look at it this way,    every  challenge   is an  opportunity.     And  anyway,   I'm  not  talking  about  investing  in  the  domestic   market.   There  are  emerging markets  that   promise  great  returns.    Look  at  China,   for  example;  th"},{"speaker":"B","text":"Come on  son,    you're  looking  at  this  too  naively,   the  Chinese  market   has exhibited  a  great deal  of  instability,   and  their  currency  has been devalued by almost  a whole  percentage point."},{"speaker":"A","text":"Fine, then!    If  that's  the  way  you  feel,   so be it.    But  you're  losing out  on  a  great  opportunity  here.   I'm  going  to go  hit up  Mom  for  the  cash."}],"vocabData":{"key":[{"word":"inevitable","type":"Adjective","definition":"unavoidable"},{"word":"nest egg","type":"common noun, singular","definition":"money that is saved to pay for something in the future, usually a house, or retirement"},{"word":"turmoil","type":"common noun, non-variable","definition":"a state of confusion, disorder, disturbance"},{"word":"stimulus package","type":"phrase","definition":"amount of money the government uses to improve the economy"},{"word":"emerging market","type":"common noun, plural","definition":"nations undergoing quick industrialization"},{"word":"devalue","type":"verb","definition":"reduced in value"},{"word":"hit up","type":"verb","definition":"ask for money"}],"supplementary":[{"word":"depreciation","type":"common noun, non-variable","definition":"an decrease in the value of something"},{"word":"appreciation","type":"common noun, non-variable","definition":"an increase in the value of something"},{"word":"inflation","type":"common noun, non-variable","definition":"an continual increase in price"},{"word":"developing economy","type":"common noun, plural","definition":"growing economy"},{"word":"rapidly developing country","type":"common noun, plural","definition":"countries undergoing quick industrialization"},{"word":"industrialized nation","type":"common noun, plural","definition":"nations that have developed factories and businesses"}]}},{"id":30,"title":"New Guy In Town II","level":"Intermediate","mp3":"/audio/englishpod_0030pb.mp3","transcript":"https://ia600408.us.archive.org/10/items/englishpod_all/englishpod_0030.html","transcriptData":[{"speaker":"A","text":"Oh,  Armand, thank you for such a thoughtful invitation!  It's really very nice of you to invite    us over for dinner, don't you think so, Ellen?"},{"speaker":"B","text":"Oh, yes of course!  We'd  love to come over.    Can I bring anything?"},{"speaker":"B","text":"I don't    want   go over  to his place for dinner! He gives me the creeps!   Why on earth  did you accept?"},{"speaker":"A","text":"Oh come on  Ellen, it will be nice to get to know  him. Besides, he's  new to the neighborhood, and it would    be  rude  to decline his  invitation."},{"speaker":"B","text":"I guess so...   You always rope me into things like this!"},{"speaker":"A","text":"Oh Oh Armand! You are too kind!"},{"speaker":"B","text":"How did I get myself into this..."}],"vocabData":{"key":[{"word":"thoughtful","type":"Adjective","definition":"showing concern for the needs and feelings of other people"},{"word":"appetite","type":"common noun, singular","definition":"a physical desire of food"},{"word":"give the creeps","type":"phrase","definition":"make someone to feel scared and uncomfortable"},{"word":"get to know","type":"verb","definition":"start to know, become familiar with someone"},{"word":"why on earth","type":"phrase","definition":"(expression to emphasize surprise or frustration)"},{"word":"decline","type":"verb","definition":"say you will not or cannot do something"},{"word":"rope into","type":"verb","definition":"convince someone to do something"}],"supplementary":[{"word":"make yourself at home","type":"phrase","definition":"polite way to ask guests to make themselves comfortable"},{"word":"hospitable","type":"Adjective","definition":"being a friendly and caring host to the guests"},{"word":"potluck","type":"common noun, singular","definition":"a party that requires everybody to bring food"},{"word":"housewaming","type":"common noun, singular","definition":"a party to celebrate moving into a new home"}]}},{"id":31,"title":"Canceling an Appointment","level":"Elementary","mp3":"/audio/englishpod_0031pb.mp3","transcript":"https://ia600408.us.archive.org/10/items/englishpod_all/englishpod_0031.html","transcriptData":[{"speaker":"A","text":"Hello, Samantha speaking."},{"speaker":"B","text":"Hi Samantha. This is Angela calling."},{"speaker":"A","text":"Oh, hi Angela, what's up?"},{"speaker":"B","text":"I'm just calling about our meeting today. I wonder, is it possible to reschedule   our   appointment in the afternoon? I have a bit of an emergency   that I need to take care of."},{"speaker":"A","text":"Let me see, it shouldn't  be too much of a problem..."},{"speaker":"B","text":"I'm  really sorry, I hope it  doesn't  inconvenience you too much, it's  just this thing came up,   and ..."},{"speaker":"A","text":"Angela, you know what, I   can't  make it to our meeting, either. Why don't  we postpone it to tomorrow afternoon at the same time?"},{"speaker":"B","text":"Sounds great. See you tomorrow."},{"speaker":"B","text":"What? No wonder she told me she couldn't  make it to the meeting, oh, no, I think she saw me..."}],"vocabData":{"key":[{"word":"what's up","type":"phrase","definition":"what is the matter"},{"word":"reschedule","type":"verb","definition":"schedule something for a different time"},{"word":"inconvenience","type":"verb","definition":"cause problem or trouble"},{"word":"come up","type":"verb","definition":"occur in an unexpected way"},{"word":"make it","type":"verb","definition":"come"},{"word":"postpone","type":"verb","definition":"decide to do something at a later time"},{"word":"no wonder","type":"phrase","definition":"not surprisingly"}],"supplementary":[{"word":"scheduling conflict","type":"common noun, singular","definition":"two or more appointments scheduled at the same time"},{"word":"call off","type":"verb","definition":"cancel"},{"word":"double-book","type":"verb","definition":"have two appointments or meetings at the same time"},{"word":"push back","type":"phrase","definition":"move a meeting or appointment to a later time"},{"word":"rain check","type":"phrase","definition":"a promise or offer to do something in the future that is not possible to do now"}]}},{"id":32,"title":"Opening a Bank Account","level":"Intermediate","mp3":"/audio/englishpod_0032pb.mp3","transcript":"https://ia600408.us.archive.org/10/items/englishpod_all/englishpod_0032.html","transcriptData":[{"speaker":"A","text":"Next, please.  May I help you, sir?"},{"speaker":"B","text":"Hello, yes, I'd  like to open   a bank account."},{"speaker":"A","text":"Certainly, I can can help you with that.  What type of account would you like to open?  A chequing or a savings account?"},{"speaker":"B","text":"What What features do they offer?"},{"speaker":"A","text":"Well, if you just take a look here, see, with our chequing account,  you can have unlimited daily transactions     for a small monthly fee, and our savings account   has a higher interest rate,    but you must carry a minimum balance of $  10,000 dollars."},{"speaker":"B","text":"I see, well, I think I'm    more interested in a chequing account; I like to have   easy access to my money."},{"speaker":"A","text":"Alright, then, with this chequing account you'll be issued   a  debit card and a cheque book.  Will you require overdraft  protection?  There is an extra fee for that."},{"speaker":"B","text":"No, that won't be  necessary."},{"speaker":"A","text":"In that case, I'll get you  to fill out  this paperwork; I'll need your  social insurance number, and two pieces of government ID. If you could just  sign here, and here, and here; we'll be all set.  Would you like to make   a    deposit today?"},{"speaker":"B","text":"Yes, I'd like to deposit    one billion    dollars."}],"vocabData":{"key":[{"word":"feature","type":"common noun, plural","definition":"interesting or important quality"},{"word":"chequing account","type":"common noun, singular","definition":"a regular account for daily transations"},{"word":"transaction","type":"common noun, plural","definition":"an occurrence in which money is transfered from one person, or one account, to another"},{"word":"savings account","type":"common noun, singular","definition":"an account for people to keep their money, usually offers a higher interest rate"},{"word":"balance","type":"common noun, singular","definition":"the amount of money in a bank account"},{"word":"access","type":"common noun, singular","definition":"a way to get to something"},{"word":"debit card","type":"common noun, singular","definition":"a card for you to take money out of your bank account"},{"word":"overdraft","type":"common noun, singular","definition":"taking out more money than what is available in the account"},{"word":"I'll get you","type":"phrase","definition":"(a way to ask people to do things)"},{"word":"I'll need your","type":"phrase","definition":"(a way to request people to provide something)"},{"word":"If you could just","type":"phrase","definition":"(a polite way of asking people to do things)"},{"word":"deposit","type":"common noun, singular","definition":"an amount of money that is put in a bank account"}],"supplementary":[{"word":"credit card","type":"common noun, singular","definition":"a card to buy things and pay for them later"},{"word":"online banking","type":"common noun, non-variable","definition":"a service that allows people to do transactions online"},{"word":"telephone banking","type":"common noun, non-variable","definition":"a service that allows people to do transactions by phone"},{"word":"cheque book","type":"common noun, singular","definition":"a book of cheques for use with a chequing account"},{"word":"terms and conditions","type":"common noun, non-variable","definition":"an outline of the rules and company policies of a particular product or service"},{"word":"high interest account","type":"common noun, singular","definition":"a savings account that offers higher than usual interest rate"},{"word":"teller","type":"common noun, singular","definition":"a person working in a bank, who helps customers with daily transactions"}]}},{"id":33,"title":"Foul!","level":"Elementary","mp3":"/audio/englishpod_0033pb.mp3","transcript":"https://ia600408.us.archive.org/10/items/englishpod_all/englishpod_0033.html","transcriptData":[{"speaker":"A","text":"Has the game started yet?"},{"speaker":"B","text":"Yeah, about 5 minutes ago."},{"speaker":"A","text":"Who’s winning?"},{"speaker":"B","text":"The Bulls, of course!"},{"speaker":"A","text":"What! That wasn’t a foul! C'mon,  ref!"},{"speaker":"B","text":"Don't  worry, Shaq always screws up   free throws."},{"speaker":"A","text":"You were right!  He didn't make the shot!"},{"speaker":"B","text":"That was a great shot!  A three pointer, yeah!"},{"speaker":"A","text":"Did you see that? He traveled and the ref didn't call it!"},{"speaker":"B","text":"This ref needs glasses.  Hey ref, open your eyes! I can't  believe    he  didn't see that!"},{"speaker":"A","text":"Okay...    end of the first quarter...   Alright,  I'm gonna make a beer run."}],"vocabData":{"key":[{"word":"foul","type":"common noun, singular","definition":"an act in sports that is against the rules"},{"word":"referee","type":"common noun, singular","definition":"person who makes sure that players act according to the rules in a game or sport (informal)"},{"word":"screw up","type":"verb","definition":"make a mistake"},{"word":"free throw","type":"common noun, plural","definition":"a chance to shoot the ball due to a foul made by the opponent"},{"word":"make the shot","type":"verb","definition":"successfully throw a ball toward a goal (sports)"},{"word":"three pointer","type":"common noun, singular","definition":"a shot that scores three points in a game"},{"word":"travel","type":"verb","definition":"take more steps with the basketball than the rule allows"},{"word":"beer run","type":"common noun, non-variable","definition":"the act of quickly going to get beer"}],"supplementary":[{"word":"court","type":"common noun, singular","definition":"place where basketball is played"},{"word":"assist","type":"common noun, singular","definition":"an action where the ball is passed that helps score a goal"},{"word":"block","type":"verb","definition":"put your body between an opposing player and the ball"},{"word":"jump shot","type":"common noun, singular","definition":"jump in the air and shoot the ball while"},{"word":"rebound","type":"verb","definition":"grab and control the ball after the other team missed a shot"},{"word":"steal","type":"verb","definition":"intercept a pass between two players of the opposite team"}]}},{"id":34,"title":"Live from Washington","level":"Upper-Intermediate","mp3":"/audio/englishpod_0034pb.mp3","transcript":"https://ia600408.us.archive.org/10/items/englishpod_all/englishpod_0034.html","transcriptData":[{"speaker":"A","text":"This  is  Madeline Wright,   for  BCC News  reporting  live from  Washington D. C.  where,   very  shortly,   the  new  President  will deliver  his inaugural address.   Just  moments  ago,   the  President was  sworn in to office;   following  the United States Constitution  the  president  swore  an  oath  to  faithfully  execute  the  office of  the presidency."},{"speaker":"B","text":"And  what  exactly  is going on  now,   Madeline?"},{"speaker":"A","text":"Well,  Tom,   true to  American  tradition,   the  band  has  just  played  Hail to the Chief,  and  the  President   has been honored  by  a  21 gun salute.   Now  we're  waiting for  the  President  to  take to the stage and  deliver   his   speech.    Tom,   it's  like  a  who's who of  the  political world here  on  Capital Hill,   with  dignitaries   representing   several different  countries."},{"speaker":"B","text":"What's the  mood  on the ground  like,   Madeline?"},{"speaker":"A","text":"In a word,   the  mood  here  is  electric.   The  excitement in  the  air is  palpable;   I've never  seen   a  larger  crowd   here  on   Capital Hill,   and  the  audience is shouting  crying,  and   embracing  each other.   On  this,   a  most historic day,   you  can  feel the  hope and  the  excitement in  the air.  The   twentieth of January   will  go down in history   as  the... Oh,    Tom,   it looks like   the  President is about to  to begin..."}],"vocabData":{"key":[{"word":"deliver","type":"verb","definition":"give information by speaking; say something officially"},{"word":"swear in","type":"verb","definition":"place someone in a new position by holding an official ceremony where he promises to do the job well"},{"word":"oath","type":"common noun, singular","definition":"official, serious promise"},{"word":"who's who","type":"phrase","definition":"important and famous people in a particular industry"},{"word":"dignitary","type":"common noun, plural","definition":"person with a high rank or position, especially in government or religion"},{"word":"palpable","type":"Adjective","definition":"so strong you can feel it, obvious"},{"word":"in a word","type":"phrase","definition":"to describe something in one word"},{"word":"fellow","type":"Adjective","definition":"used to describe people of the same group"},{"word":"go down in history","type":"phrase","definition":"be remembered"}],"supplementary":[{"word":"ceremony","type":"common noun, singular","definition":"activity or action that are the official part of a formal official state or social occasion"},{"word":"festivities","type":"common noun, non-variable","definition":"celebration, enjoyment, and happiness"},{"word":"president-elect","type":"common noun, singular","definition":"a person who has been voted president, but not yet officially  made president"},{"word":"parade","type":"common noun, singular","definition":"a public celebration of an event where many people move down a street in groups or on vehicles; usually involves a marching band"},{"word":"term","type":"common noun, singular","definition":"time allowed for one person to remain President after one election; in America a presidential term is four years"},{"word":"affirmation","type":"common noun, singular","definition":"a show of agreement or dedication"},{"word":"inaugural luncheon","type":"phrase","definition":"formal lunch celebrating the inauguration"}]}},{"id":35,"title":"He's not a Good Fit","level":"Intermediate","mp3":"/audio/englishpod_0035pb.mp3","transcript":"https://ia600408.us.archive.org/10/items/englishpod_all/englishpod_0035.html","transcriptData":[{"speaker":"A","text":"So, Lauren,   I  just  wanted  to talk  to    you  quickly  about our  new   customer support  representative,   Jason Huntley."},{"speaker":"B","text":"Sure,   what's up?"},{"speaker":"A","text":"Basically,   I've  got  a few   concerns about   him,   and  the bottom line  is,   I   don't   think  he's  a good fit  for   our  company."},{"speaker":"B","text":"Okay... what    makes   you   say  that?    I    thought   you  were pleased with  his  overall   performance.  Didn't    you  just   tell  me  last   week   how   impressed  you  were    with   his  attitude?"},{"speaker":"A","text":"Yeah,    his  attitude  is   great,    but    he's  really   unreliable.    Sometimes  he's    really   productive,    but   then  other  times... take  last   Tuesday   for instance,      he   was  forty-five   minutes   late   for   our  morning meeting!"},{"speaker":"B","text":"Well,    I'm  sure  he  had a  perfectly good  reason..."},{"speaker":"A","text":"But   that's   not  the  only  thing...  you  know,    he  really  doesn't   have  the  best  work ethic,   I'm   constantly   catching  him  on  MSN   and  Facebook  when  he   should  be talking    to  clients."},{"speaker":"B","text":"Yeah,   but  come on,    Geoff,    as if you   don't  check  Facebook at   work.    Look,     you   hired  this  guy,    we've  invested    a lot   of   time  and  money   in  his  training,   so  now  it's up to you to coach  him.    Make it work,   Geoff!"},{"speaker":"A","text":"Make it work, Geoff.  You   would   say   that,   wouldn't    you,   he  is  your  cousin;     what    a   jerk,   make  me  hire your  stupid,  useless,    cousin."}],"vocabData":{"key":[{"word":"concern","type":"common noun, plural","definition":"worry"},{"word":"overall","type":"Adjective","definition":"general"},{"word":"performance","type":"common noun, singular","definition":"how well someone does their job"},{"word":"unreliable","type":"Adjective","definition":"not able to be trusted"},{"word":"productive","type":"Adjective","definition":"doing a lot in a short amount of time"},{"word":"perfectly good","type":"phrase","definition":"having no problems, just fine"},{"word":"work ethic","type":"common noun, non-variable","definition":"attitude to work"},{"word":"coach","type":"verb","definition":"teach and train someone new skills"}],"supplementary":[{"word":"poor judgment","type":"phrase","definition":"regularly makes bad decisions"},{"word":"poor work ethic","type":"phrase","definition":"bad attitude to work"},{"word":"dependable","type":"Adjective","definition":"able to be trusted and relied upon"},{"word":"a bad apple","type":"phrase","definition":"a bad member of a group who makes things more difficult for others"},{"word":"unproductive","type":"Adjective","definition":"not productive, not doing a lot"}]}},{"id":36,"title":"I`m Sorry I Love You III","level":"Elementary","mp3":"/audio/englishpod_0036pb.mp3","transcript":"https://ia600408.us.archive.org/10/items/englishpod_all/englishpod_0036.html","transcriptData":[{"speaker":"A","text":"Steven! Where have  you been?  I've  been trying to get a hold of  you  for hours!"},{"speaker":"B","text":"I... um... there was an emergency at work, so..."},{"speaker":"A","text":"I was waiting for you in the restaurant for three hours! And you didn't  even have the decency to call me!  Do you have any idea how embarrassed I was?"},{"speaker":"B","text":"Honey, I promise this won't  happen again, it's    just that I..."},{"speaker":"A","text":"Yeah, right.  I've  heard it all before.  I'm  not going to take  any more    of your empty promises. This is the fifth time you've  stood me up  in two weeks! You need to get your priorities straight.  I'm  tired of you putting your job first all the time!"},{"speaker":"B","text":"Come on, Veronica, that's  not fair.  I do care about you a lot,  you know that.  I tried to ..."},{"speaker":"A","text":"You know what? Maybe we should just take a break. I need some time to think about where this relationship is heading."},{"speaker":"B","text":"But...Veronica, would you just listen to me? There was a fire alarm at my office building today and I was stuck..."}],"vocabData":{"key":[{"word":"get hold of","type":"verb","definition":"find or contact someone"},{"word":"decency","type":"common noun, non-variable","definition":"polite or moral behavior"},{"word":"do you have any idea","type":"phrase","definition":"do you know"},{"word":"stand (someone) up","type":"verb","definition":"fail to keep the appointment to meet someone"},{"word":"get your priorities straight","type":"phrase","definition":"realize what is more important"},{"word":"head","type":"verb","definition":"go towards a specified direction or place"}],"supplementary":[{"word":"break up","type":"verb","definition":"end a relationship"},{"word":"cheat on (someone)","type":"verb","definition":"kiss or have sex with someone who is not your boyfriend, girlfriend, wife or husband"},{"word":"get back together","type":"phrase","definition":"restart a relationship that ended"},{"word":"ex-boyfriend","type":"common noun, singular","definition":"person who used to be your boyfriend"}]}},{"id":37,"title":"Chinese New Year","level":"Intermediate","mp3":"/audio/englishpod_0037pb.mp3","transcript":"https://ia600408.us.archive.org/10/items/englishpod_all/englishpod_0037.html","transcriptData":[{"speaker":"A","text":"I'm so  excited  about Chinese New Year!  When do I get to visit Grandma? Grandma makes the best dumplings  in the world!"},{"speaker":"B","text":"Ha ha, right. Sounds to me like you're  more excited about the dumplings than seeing your Grandma."},{"speaker":"A","text":"Of course  I miss Grandma, too. I bet she's  gonna teach me how to play Mahjong! Hey, Dad, are you going to buy me firecrackers this year? We're  going to have the best fireworks! I'm  really  looking forward to    lighting them!"},{"speaker":"B","text":"Son, firecrackers aren't  toys; they're  dangerous!"},{"speaker":"A","text":"No,  fireworks are awesome!"},{"speaker":"B","text":"Whoa, don't  you remember? Last year when I set off the firecrackers, you covered both your ears and hid behind your mother?"},{"speaker":"A","text":"Dad! I was scared because... because I saw a bug. That's  all."},{"speaker":"B","text":"Hahaha... really?"},{"speaker":"A","text":"Oh, and I can't  wait to watch the   dragon dance!  Dad, can I sit on your shoulders this time?"},{"speaker":"B","text":"Hey, I offered last year..."},{"speaker":"A","text":"Well, I... anyways, I was just thinking of the red envelopes. I wanna make a list of all the things I'm gonna buy  with my red envelope money!  I can't  wait! I'm gonna have so much money!  Mom, can I get a pen and a piece  of  paper?"},{"speaker":"A","text":"I want a new transformer, no, two transformers...the Optimus Prime, and...maybe the wheeljack? I'll  get a    PSP game, hahaha, and I'll  buy the entire class lunch at MacDonald's..."}],"vocabData":{"key":[{"word":"excited","type":"Adjective","definition":"very happy about something"},{"word":"dumpling","type":"common noun, plural","definition":"small lump of dough that is steamed or boiled, traditional Chinese New Year food"},{"word":"firecracker","type":"common noun, plural","definition":"a cylinder filled with explosive substance that makes loud noise when it explodes"},{"word":"look forward to","type":"phrase","definition":"excited and happy about something that will happen in the future"},{"word":"can't wait","type":"phrase","definition":"eager or excited about doing something or for something to begin"},{"word":"set off","type":"verb","definition":"cause something to explode"}],"supplementary":[{"word":"Lunar New Year","type":"phrase","definition":"Chinese New Year, based on the cycles of the moon"},{"word":"Spring Festival","type":"phrase","definition":"Chinese New Year"},{"word":"lunar calendar","type":"common noun, singular","definition":"a calendar based on the cycles and phases of the moon"},{"word":"can hardly wait","type":"phrase","definition":"very excited about something that will happen in the future"},{"word":"impatient","type":"Adjective","definition":"not happy or willing to wait for something"}]}},{"id":38,"title":"Buying a Car","level":"Intermediate","mp3":"/audio/englishpod_0038pb.mp3","transcript":"https://ia600408.us.archive.org/10/items/englishpod_all/englishpod_0038.html","transcriptData":[{"speaker":"A","text":"Hi  there,   can I help you folks?"},{"speaker":"B","text":"I'm  just browsing; seeing what's  on  the  lot.   My daughter wants a car for her birthday, you know how it is."},{"speaker":"A","text":"She is right, you know.  Kids these days  all have cars. Let me show you something we just got in:  a 1996 sedan. Excellent gas mileage, it has dual airbags and anti lock brakes;   a perfect vehicle for a young driver."},{"speaker":"B","text":"I see...    What can you tell me about this one?"},{"speaker":"A","text":"Oh, that's   just  an old World War Two   tank  that  we use for TV commercials.  Now about this sedan..."},{"speaker":"B","text":"Whoa, whoa wait a minute. Tell me more about this tank."},{"speaker":"A","text":"Well,  Sir, if you are looking for  quality and safety then look no further!  Three inches of reinforced steel protect your daughter from short range missile attacks."},{"speaker":"B","text":"Does the sedan protect her from missile attacks?"},{"speaker":"A","text":"It does not."},{"speaker":"B","text":"Well, I  don't  know.   Let   me   sleep on it."},{"speaker":"A","text":"Did I mention the tank is a tank?"},{"speaker":"B","text":"I'll take it!"}],"vocabData":{"key":[{"word":"browse","type":"verb","definition":"look around to see if anything is interesting"},{"word":"sedan","type":"common noun, singular","definition":"a car with four doors"},{"word":"gas mileage","type":"common noun, singular","definition":"the amount of gas used per mile"},{"word":"dual airbags","type":"common noun, plural","definition":"two airbags, one for the driver and one for the passenger"},{"word":"brake","type":"common noun, plural","definition":"a device that slows down or stops something"},{"word":"vehicle","type":"common noun, singular","definition":"something that carries people from one place to another"},{"word":"reinforce","type":"Adjective","definition":"strengthen, make something stronger"},{"word":"sleep on it","type":"phrase","definition":"think about something further and make a decision later (informal)"}],"supplementary":[{"word":"minivan","type":"common noun, singular","definition":"a vehile that is larger than a car, has four doors and is shaped like a box"},{"word":"SUV","type":"common noun, singular","definition":"a large vehicle that is suitable to run on rough surfaces"},{"word":"sale","type":"common noun, singular","definition":"an event where goods or services are sold at a price lower than usual"},{"word":"promotion","type":"common noun, singular","definition":"something (usually advertising) that is done to increase sales"},{"word":"discount","type":"common noun, singular","definition":"the amount of reduction in price"},{"word":"bargain","type":"common noun, singular","definition":"something bought or sold at a price lower than its actual value"}]}},{"id":39,"title":"My New Boyfriend","level":"Elementary","mp3":"/audio/englishpod_0039pb.mp3","transcript":"https://ia600408.us.archive.org/10/items/englishpod_all/englishpod_0039.html","transcriptData":[{"speaker":"A","text":"Irene! I heard you  were on a date last night! So, how did it go?  I want all the juicy details!"},{"speaker":"B","text":"Um... well, actually, we had a fantastic time last night. He was...amazing!"},{"speaker":"A","text":"Okay, now you really have to fill me in.  What's he like?"},{"speaker":"B","text":"He's  really good looking; he's  quite tall, around 6'1'',  he's  in   his early thirties,   and he's  got the most beautiful dark brown eyes..."},{"speaker":"A","text":"He sounds hot!  What does he do for a living?"},{"speaker":"B","text":"You know what, this is the best part. David is a junior investment banker at Fortune Bank, so he's  got a  great career path ahead of him!"},{"speaker":"A","text":"Hold on a sec,   his name is David?"},{"speaker":"B","text":"Yeah?"},{"speaker":"A","text":"That's  my brother!"}],"vocabData":{"key":[{"word":"hear","type":"verb","definition":"get the information from someone"},{"word":"juicy detail","type":"common noun, plural","definition":"exciting pieces of information"},{"word":"fill me in","type":"verb","definition":"give me more information"},{"word":"good looking","type":"Adjective","definition":"handsome"},{"word":"hot","type":"Adjective","definition":"attractive"},{"word":"career path","type":"common noun, singular","definition":"a planned, logical progression of jobs within a profession throughout working life"}],"supplementary":[{"word":"attractive","type":"Adjective","definition":"have a pleasing appearance"},{"word":"sexy","type":"Adjective","definition":"attractive, exciting and appealing"},{"word":"tender","type":"Adjective","definition":"very loving and gentle"},{"word":"handsome","type":"Adjective","definition":"good looking"}]}},{"id":40,"title":"Can I ask you a favor?","level":"Elementary","mp3":"/audio/englishpod_0040pb.mp3","transcript":"https://ia600408.us.archive.org/10/items/englishpod_all/englishpod_0040.html","transcriptData":[{"speaker":"A","text":"Um,   sorry  to bother  you um... my  name   is  Rachel.    I'm  new here.    Can I ask you a favor?"},{"speaker":"B","text":"Hi  Rachel,   welcome   on board.     I'm afraid   I  can't   help   you  right now.   I'm  getting ready   for  a   very   important   meeting."},{"speaker":"A","text":"Excuse me,    but  can I bother you  for  a   sec?"},{"speaker":"A","text":"Sorry to interrupt you  Sean,   could   you  do me a quick favor?"},{"speaker":"A","text":"Geeze!    I   just   want   to know  where  the   bathroom    is!     What's  wrong   with  you  people!"}],"vocabData":{"key":[{"word":"bother","type":"verb","definition":"annoy or disturb someone"},{"word":"can I ask you a favor","type":"phrase","definition":"ask someone to do a kind and helpful thing"},{"word":"on board","type":"phrase","definition":"to the company"},{"word":"can I bother you","type":"phrase","definition":"polite way of asking if you can interrupt someone"},{"word":"instead","type":"General Adverb","definition":"in the place of"},{"word":"sorry to interrupt","type":"phrase","definition":"phrase used to begin speaking to someone when they are busy doing or saying something"},{"word":"do me a quick favor","type":"phrase","definition":"do a small, helpful thing that won't take a long time"}],"supplementary":[{"word":"help me out","type":"phrase","definition":"help me"},{"word":"give me a hand","type":"phrase","definition":"help me with something"},{"word":"annoy","type":"verb","definition":"cause someone to feel slightly angry"},{"word":"bug","type":"verb","definition":"bother or annoy someone (informal)"},{"word":"disrupt","type":"verb","definition":"stop someone from working, or doing something"}]}},{"id":41,"title":"Movie Trailer","level":"Intermediate","mp3":"/audio/englishpod_0041pb.mp3","transcript":"https://ia600408.us.archive.org/10/items/englishpod_all/englishpod_0041.html","transcriptData":[{"speaker":"A","text":"In a digital world, even the strongest must fight for survival.  Two people, possess a secret so valuable, so powerful, they have to defend it at all costs."},{"speaker":"B","text":"I don't care where they are, I  don't care what it takes...  you find them and bring them to me!"},{"speaker":"A","text":"They only had one chance! And their chance was to fight  back!"},{"speaker":"A","text":"With a little help from a Governor..."},{"speaker":"A","text":"Nothing will prevent them from doing their job! Double the action."},{"speaker":"A","text":"Triple the excitement."},{"speaker":"A","text":"This  summer... nothing   will stand in their way."},{"speaker":"B","text":"I'm  going to make   him  an  offer   he   can't  refuse."},{"speaker":"A","text":"Two  hosts,   one   podcast,   coming to  a   theater  near   you."}],"vocabData":{"key":[{"word":"possess","type":"verb","definition":"have or own"},{"word":"survival","type":"common noun, non-variable","definition":"the state of living and being alive"},{"word":"defend","type":"verb","definition":"fight in order to keep something or someone safe"},{"word":"rough","type":"phrase","definition":"be tough,difficult, violent"},{"word":"no matter what","type":"common noun, singular","definition":"even if it's difficult"},{"word":"get down","type":"phrase","definition":"lower your body, not standing"},{"word":"stand in someone's way","type":"phrase","definition":"prevent a person from doing something"},{"word":"at all costs","type":"phrase","definition":"regardless of the cost involved, even if it's difficult"}],"supplementary":[{"word":"comedy","type":"common noun, singular","definition":"a funny movie"},{"word":"romantic comedy","type":"common noun, singular","definition":"a funny movie where two people fall in love"},{"word":"animated film","type":"common noun, singular","definition":"a movie made with a series of drawings; not with live people"},{"word":"thriller","type":"common noun, singular","definition":"a very exciting film"},{"word":"drama","type":"common noun, singular","definition":"a movie about a serious subject"}]}},{"id":42,"title":"I Need More Time","level":"Elementary","mp3":"/audio/englishpod_0042pb.mp3","transcript":"https://ia600408.us.archive.org/10/items/englishpod_all/englishpod_0042.html","transcriptData":[{"speaker":"A","text":"So,    Casey,    how  are  things  going   with  the  photos for  the  press kit?"},{"speaker":"B","text":"Yeah,    I've   been meaning  to talk  to  you   about   that.    I   might  need  to ask  for  an  extension  on  that  deadline."},{"speaker":"A","text":"You've   had   over   a   month   to get    this  finalized!     Why   are things   delayed?"},{"speaker":"B","text":"Well,    the thing is,    we  ran into a lot  of  problems..."},{"speaker":"A","text":"I'm  not   looking  for   excuses  here.     I   just   want   to get  this  finished  on time!"},{"speaker":"B","text":"I  know,   and  I   apologize   for   the  delay.    But   some  things  were  just    beyond my control.        I   had  trouble  booking  the  the  photographer,   and   then  Michael  was  sick  for three  weeks,   so I   couldn't  include  him   in  the   photos,   and  the  design team   lost  all  the  files,  so  I  had  to redo  the  pictures."},{"speaker":"A","text":"I'm   not  going to put this off  any longer,   Casey!   I  want  those  photos  ASAP!"}],"vocabData":{"key":[{"word":"press kit","type":"common noun, singular","definition":"group of photos, documents, articles, and information about a company given to reporters, newspapers, magazines, etc."},{"word":"I've been meaning to","type":"verb","definition":"intend, plan to do something"},{"word":"extension","type":"common noun, singular","definition":"make a deadline longer, extra time to do something"},{"word":"deadline","type":"common noun, singular","definition":"the date when something must be finished"},{"word":"finalize","type":"verb","definition":"finish, decide on all the details"},{"word":"delay","type":"verb","definition":"late"},{"word":"run into","type":"phrase","definition":"meet or encounter"},{"word":"beyond someone's control","type":"phrase","definition":"not able to control something"},{"word":"put (something) off","type":"verb","definition":"plan to do something later"}],"supplementary":[{"word":"due","type":"Adjective","definition":"the date when something must be finished or happen"},{"word":"behind","type":"Adjective","definition":"late with a project or things you have to do"},{"word":"hold up","type":"phrase","definition":"delay or slow something"},{"word":"tight deadline","type":"phrase","definition":"a deadline that is really difficult to meet"}]}},{"id":43,"title":"Applying for a Visa","level":"Daily Life","mp3":"/audio/englishpod_0043pb.mp3","transcript":"https://ia600408.us.archive.org/10/items/englishpod_all/englishpod_0043.html","transcriptData":[{"speaker":"A","text":"So,    you're  applying   for  a  B2  visa,   where   is  your  final  destination  and  what's  the   purpose  of  your    trip   to  the  United States?"},{"speaker":"B","text":"I'm   going  to visit  my  brother;   he's  just  had  a  baby.    He  lives   in  Minneapolis."},{"speaker":"A","text":"And   how long  do you  you   plan  to remain   in  the  United States?"},{"speaker":"B","text":"I'll  be  here  for  approximately   three  weeks.   See,   here's  my return ticket for   the  twenty-sixth of March."},{"speaker":"A","text":"And,  who   is sponsoring   your  trip?"},{"speaker":"B","text":"My  brother,   here,   this   is  an invitation  letter   from  him.   I  will stay  with  him   and  his  family  in their  home."},{"speaker":"A","text":"Alright,   tell  me  about  the  ties   you  have  to   your  home country."},{"speaker":"B","text":"Well,   I   own   a  house;   actually,   I'm  leaving my  dog   there    with  my neighbors.   I  have  a  car   at   home,  and  oh,   my  job!   I'm  employed by   Tornel   as  an  engineer.   Actually,   I  only  have  three  weeks'  vacation,   so I  have  to go back   to   work  at  the   end   of  March."},{"speaker":"A","text":"And   what evidence  do  you  have   that    you  are  financially independent?"},{"speaker":"B","text":"Well,   I   do  have   assets  in   my  country;   like  I  said,   I  own  a    house,   and  see,   here's  a  bank statement  showing  my   investments,   and  my   bank balance."},{"speaker":"A","text":"I'm  sorry,   sir,    we  cannot grant   you  a  B2  visa   at this time,    instead,   you  are   granted  a   resident visa!    Congratulations,   you   are   the   millionth  person   to apply  for   a  visa!    You   win!    Congratulations!"}],"vocabData":{"key":[{"word":"remain","type":"principle verb, infinitive","definition":"stay"},{"word":"approximately","type":"Adjective","definition":"an amount that is almost, but not exact"},{"word":"sponsor","type":"principle verb, present progressive","definition":"someone who takes the responsibility for a person applying for a visa"},{"word":"invitation letter","type":"common noun, singular","definition":"a formal letter asking a person to come to a place"},{"word":"tie","type":"common noun, plural","definition":"responsibility that links a person to a place or a thing"},{"word":"evidence","type":"common noun, non-variable","definition":"something that shows the thing is true"},{"word":"financially independent","type":"phrase","definition":"have enough money to live alone; do not need help with money"},{"word":"assets","type":"common noun, plural","definition":"things that are worth money that you own"},{"word":"grant","type":"principle verb, present simple","definition":"let you have something"}],"supplementary":[{"word":"visa officer","type":"common noun, singular","definition":"a person who's job is to interview people for visas"},{"word":"resident permit","type":"common noun, singular","definition":"an official document that allows you to live in a country"},{"word":"embassy","type":"common noun, singular","definition":"the group of people who represent your country in another country, located in the capital city"},{"word":"application form","type":"common noun, singular","definition":"a piece of paper you have to put information on to make a formal request"},{"word":"consulate","type":"common noun, singular","definition":"government building representing your country in another country; not  located in a capital city"}]}},{"id":44,"title":"Small Talk","level":"Elementary","mp3":"/audio/englishpod_0044pb.mp3","transcript":"https://ia600408.us.archive.org/10/items/englishpod_all/englishpod_0044.html","transcriptData":[{"speaker":"A","text":"Morning."},{"speaker":"B","text":"Hi there,  Mr. Anderson!   How are you on this fine morning?"},{"speaker":"A","text":"Fine, thank you."},{"speaker":"B","text":"It sure is cold this morning, isn't it?  I barely even got out of bed!"},{"speaker":"A","text":"Yeah. It’s pretty cold, alright."},{"speaker":"B","text":"Did you catch the news this morning?  I heard that there was a fire on Byron Street."},{"speaker":"A","text":"No, I didn't hear about that."},{"speaker":"B","text":"Did you happen to watch the football game last night? The Patriots scored in the last minute!"},{"speaker":"A","text":"No, I don’t like football."},{"speaker":"B","text":"Oh… By the way, I saw you with your daughter at the office Christmas party.  She is really beautiful!"},{"speaker":"A","text":"She’s my wife!  Oh, here’s my floor!  Nice talking to you.  Goodbye."},{"speaker":"B","text":"Sir, this is the fifty-sixth floor!  We are on the seventieth!"},{"speaker":"A","text":"That’s okay, I'll take the stairs!"}],"vocabData":{"key":[{"word":"It sure is","type":"phrase","definition":"Affirmation or confirmation of something"},{"word":"barely even","type":"General Adverb","definition":"hardly, could almost not"},{"word":"catch","type":"verb","definition":"hear, listen to"},{"word":"Did you happen to","type":"phrase","definition":"By chance or casualty someone did something"},{"word":"By the way","type":"phrase","definition":"phrased used to interject another topic in a conversation"},{"word":"take the stairs","type":"phrase","definition":"go up the stairs, use the stairs"}],"supplementary":[{"word":"small talk","type":"phrase","definition":"short, firendly conversation about things that are not important"},{"word":"chat","type":"verb","definition":"talk (informal)"},{"word":"network","type":"verb","definition":"talk with people to form business connections"},{"word":"awkward","type":"Adjective","definition":"difficult to deal with"},{"word":"uncomfortable","type":"Adjective","definition":"a feeling of being embarrassed or uneasy"}]}},{"id":45,"title":"I'm Sorry I Love You IV","level":"Intermediate","mp3":"/audio/englishpod_0045pb.mp3","transcript":"https://ia600408.us.archive.org/10/items/englishpod_all/englishpod_0045.html","transcriptData":[{"speaker":"A","text":"... so, I said, \"let's take a break .\" And since that night, I've   been waiting for him to call, but I still haven't heard from him.  You don't think he's seeing someone else, do you?"},{"speaker":"B","text":"Come on, don't be so dramatic!  I'm sure everything is going to work out just fine."},{"speaker":"A","text":"You think so?  Oh, no! How can he do this to me?  I'm sure he's cheating on me! Why else wouldn't he call?"},{"speaker":"B","text":"But, you two are on a break.    Theoretically he can do whatever he likes."},{"speaker":"A","text":"He's the love of my life! I've really messed this up."},{"speaker":"B","text":"Come on, hon. Pull yourself together. It's going to be alright."},{"speaker":"A","text":"But I... I still love him! And it's all my fault! I can't believe how immature and selfish I was being. I mean, he is a firefighter, it's not like he can just leave someone in a burning building and meet me for dinner. I've totally messed this up!"},{"speaker":"B","text":"You know what, Veronica, I think you should make the first step. I'm sure he'll forgive you..."},{"speaker":"A","text":"No, this is not gonna happen! I... I've ruined everything...."},{"speaker":"B","text":"Hey... do you hear something?   Guess what? It's your lovely firefighter!"}],"vocabData":{"key":[{"word":"seeing someone","type":"phrase","definition":"dating or going out with someone"},{"word":"cheat on (someone)","type":"verb","definition":"having a romantic relationship with someone else"},{"word":"on a break","type":"phrase","definition":"a relationship that is temporarily on hold"},{"word":"pull yourself together","type":"phrase","definition":"calm down, relax, organize your thoughts"},{"word":"immature","type":"Adjective","definition":"acting in a childish way"},{"word":"selfish","type":"Adjective","definition":"having or showing concern only for yourself and not for the needs or feelings of other people"},{"word":"ruin","type":"verb","definition":"damage, destroy something"}],"supplementary":[{"word":"break up","type":"verb","definition":"end a relationship"},{"word":"dump (someone)","type":"verb","definition":"end a relationship when the other person does not want to (informal)"},{"word":"partner","type":"common noun, singular","definition":"serious, long-term boyfriend of girlfriend"},{"word":"ex","type":"common noun, singular","definition":"former boyfriend, girlfriend, husband, or wife (informal)"},{"word":"love life","type":"phrase","definition":"romantic social life"},{"word":"it's not the end of the world","type":"phrase","definition":"don't worry,  it is not so serious"}]}},{"id":46,"title":"Emergency Room","level":"Upper-Intermediate","mp3":"/audio/englishpod_0046pb.mp3","transcript":"https://ia600408.us.archive.org/10/items/englishpod_all/englishpod_0046.html","transcriptData":[{"speaker":"A","text":"Help!  Are you a doctor?    My poor little Frankie has stopped breathing!  Oh my gosh, Help me!  I tried to perform CPR, but I just don’t know if I could get any air into his lungs!  Oh, Frankie!"},{"speaker":"B","text":"Ellen, get him hooked up to a monitor!  Someone page Dr. Howser.  Get the patient to hold still, I can’t get a pulse! Okay, he’s on the monitor.  His BP is falling! He's flatlining!"},{"speaker":"A","text":"NOOOOOO! Frankie! Nurse! Do something!"},{"speaker":"B","text":"Someone get her out of here! Get me the defibrillator.  Okay, clear! Again!  Clear!  Come on! dammit!   I'm not letting you go! Clear! I've   got a pulse!"},{"speaker":"B","text":"The patient is in acute respiratory failure, I think we’re going to have to intubate!"},{"speaker":"A","text":"Doctor, oh, thank god!  How is he?"},{"speaker":"B","text":"We managed to stabilize Frankie, but he’s not out of the woods  yet; he’s still in critical condition. We’re moving him to intensive care, but…"},{"speaker":"A","text":"Doctor, just do whatever it takes. I just want my little Frankie to be okay.  I couldn’t imagine life without my little hamster!"}],"vocabData":{"key":[{"word":"intensive care","type":"common noun, singular","definition":"place in the hospital where very ill patients are treated"},{"word":"critical condition","type":"Adjective","definition":"state where a person is very sick or injured, and has a risk of dying"},{"word":"out of the woods","type":"phrase","definition":"out trouble or danger,"},{"word":"stabilize","type":"verb","definition":"stop a patient from getting worse"},{"word":"intubate","type":"verb","definition":"place a plastic tube in a persons mouth to facilitate breathing"},{"word":"acute respiratory failure","type":"common noun, singular","definition":"condition in which a person stops breathing"},{"word":"defibrillator","type":"common noun, singular","definition":"electric machine that shocks the heart and makes it start working again"},{"word":"BP","type":"common noun, singular","definition":"blood pressure"},{"word":"flatline","type":"verb","definition":"condition where the heart stops working"},{"word":"pulse","type":"common noun, singular","definition":"the number of times your heart beats per minute"},{"word":"hold still","type":"phrase","definition":"not move"},{"word":"hook up","type":"phrase","definition":"connect someone or something to a machine"},{"word":"CPR","type":"common noun, singular","definition":"procedure in which air is blown into another persons lungs though their mouth"}],"supplementary":[{"word":"chest compressions","type":"common noun, plural","definition":"act of pushing on someone's chest to restart their heart"},{"word":"resuscitate","type":"verb","definition":"make someone who stopped breathing start breathing again"},{"word":"defibrillator","type":"common noun, singular","definition":"machine that uses electricity to shock and restart the heart"},{"word":"ER","type":"phrase","definition":"Emergency Room; place in the hospital where you bring sick or injured people in an emergency"},{"word":"ICU","type":"phrase","definition":"Intensive Care Unit; place in the hospital where very sick or severely injured people are taken care of"}]}},{"id":47,"title":"Just In Time Strategy","level":"Advanced","mp3":"/audio/englishpod_0047pb.mp3","transcript":"https://ia600408.us.archive.org/10/items/englishpod_all/englishpod_0047.html","transcriptData":[{"speaker":"A","text":"I  called  this  meeting   today in order  to discuss   our  manufacturing plan.    As   I'm   sure   you're  all  aware,    with   the   credit crunch,   and  the  global financial crisis,   we're   obligated   to look  for   more cost efficient  ways  of   producing   our    goods.    We   don't   want  to have  to be  looking  at  redundancies.   So,    we've   outlined   a  brief   plan   to implement  the   just-in-time  philosophy."},{"speaker":"B","text":"We   have two   basic   points   that  we want   to focus on.    First of all,   we  want  to reduce  our    lead time."},{"speaker":"B","text":"Well,   we  want  to reduce   production and  delivery lead timesfor  better  overall  efficiency."},{"speaker":"A","text":"Right, production lead times can be reduced by moving work stations closer together, reducing queue length, like for example, reducing the number of jobs waiting to be processed at a given machine, and improving the coordination and cooperation between successive processes. Delivery lead times can be reduced through close cooperation with suppliers, possibly by inducing suppliers to locate closer to the factory or working with a faster shipping company."},{"speaker":"B","text":"The second point is that we want to require supplier quality assurance and implement a zero defects quality program. We currently have far too many errors that lead to defective items and therefore, they must be eliminated. A quality control at the source program must be implemented to give workers the personal responsibility for the quality of the work they do, and the authority to stop production when something goes wrong."},{"speaker":"A","text":"Exactly.  Well, let's look at how we're going to put this plan into action.  First...(fade out)"}],"vocabData":{"key":[{"word":"obligate","type":"verb","definition":"make a person do something because it is the right thing to do"},{"word":"cost efficient","type":"phrase","definition":"cheap, not expensive"},{"word":"redundancy","type":"common noun, plural","definition":"layoff, the ending of workers' employment"},{"word":"implement","type":"verb","definition":"put a plan into action"},{"word":"lead time","type":"common noun, singular","definition":"the time between the beginning of a process and its end"},{"word":"efficiency","type":"common noun, singular","definition":"the ability to produce something more quickly"},{"word":"work station","type":"common noun, plural","definition":"an area where a person works and does his job"},{"word":"given","type":"Adjective","definition":"used to refer to a specific time, place or thing"},{"word":"defective","type":"Adjective","definition":"having a mistake or error that makes something not perfect"},{"word":"defect","type":"common noun, plural","definition":"a mistake or error that makes something not perfect"}],"supplementary":[{"word":"inventory","type":"common noun, non-variable","definition":"supply of goods stored in a place"},{"word":"carrying cost","type":"phrase","definition":"the cost of holding inventory"},{"word":"warehouse","type":"common noun, singular","definition":"large building where products or goods are stored"},{"word":"stock","type":"common noun, non-variable","definition":"the supply of goods for sale, or available"},{"word":"bottleneck","type":"common noun, singular","definition":"something that slows a process down"}]}},{"id":48,"title":"Carnival in Rio!","level":"Intermediate","mp3":"/audio/englishpod_0048pb.mp3","transcript":"https://ia600408.us.archive.org/10/items/englishpod_all/englishpod_0048.html","transcriptData":[{"speaker":"A","text":"I can't believe we're here!  Carnival in Rio! Seriously, this is like a once in a lifetime opportunity! Can you believe it?  We're here at the biggest party in the world!"},{"speaker":"B","text":"I know!  We're so lucky that we found tickets for the Sambadrome!  Good thing we found that ticket scalper."},{"speaker":"A","text":"Look!  It's starting!  Wow, this is amazing!  Look at how many dancers there are.  Oh my gosh! The costumes are so colorful! This is so cool!"},{"speaker":"B","text":"It says here that the school that is dancing now is one of the oldest and most prestigious samba schools in Rio."},{"speaker":"A","text":"No kidding! Look at them, they're amazing! Look at that girl on the top of that float!  She must be the carnival queen! Move over there  so I can get a picture of you!"},{"speaker":"B","text":"Ok.  Hurry up take the picture!"},{"speaker":"B","text":"Oh really....  no I can't. No really, I don't know how to dance! Honey I'll see you later!"},{"speaker":"A","text":"Patrick!  Don't just leave me here!"}],"vocabData":{"key":[{"word":"prestigious","type":"Adjective","definition":"being respected and admired for being successful or important"},{"word":"ticket scalper","type":"common noun, singular","definition":"person that buys and re-sales tickets at a higher price"},{"word":"once in a lifetime","type":"phrase","definition":"something that happens very rarely, not common"},{"word":"float","type":"common noun, singular","definition":"a vehicle with a platform used to carry an exhibit in a parade"},{"word":"good thing","type":"phrase","definition":"it is good that something happened"}],"supplementary":[{"word":"stadium","type":"common noun, singular","definition":"a large structure where sport events are held"},{"word":"festival","type":"common noun, singular","definition":"a special time or event when people gather to celebrate something"},{"word":"Brasilia","type":"phrase","definition":"the capital of Brazil"},{"word":"take a chance","type":"phrase","definition":"take a risk, do something that you would not usually do"},{"word":"ditch","type":"verb","definition":"leave someone on purpose (informal)"}]}},{"id":49,"title":"Daddy Please!","level":"Intermediate","mp3":"/audio/englishpod_0049pb.mp3","transcript":"https://ia600408.us.archive.org/10/items/englishpod_all/englishpod_0049.html","transcriptData":[{"speaker":"A","text":"Hey daddy!  You look great today;  I like your tie! By the way, I was wondering can I…"},{"speaker":"B","text":"NO!"},{"speaker":"A","text":"I haven’t even told you what it is yet!"},{"speaker":"B","text":"Okay, okay, what do you want?"},{"speaker":"A","text":"Do you think  I could borrow the car?  I'm going to a concert tonight."},{"speaker":"B","text":"Um.. I don't think so.   I need the car tonight to pick up your mother."},{"speaker":"A","text":"Ugg! I told you about it last week! Smelly Toes is playing,   and Eric asked if I would go with him!"},{"speaker":"B","text":"Who’s this Eric guy?"},{"speaker":"A","text":"Duh! He’s like the hottest and most popular guy at school! Come on,   dad! Please!"},{"speaker":"B","text":"No can do... sorry."},{"speaker":"A","text":"Fine then! Would you mind giving me 100 bucks?"},{"speaker":"B","text":"No way!"},{"speaker":"A","text":"That's so unfair!"}],"vocabData":{"key":[{"word":"by the way","type":"phrase","definition":"used to change the topic of a conversation"},{"word":"wonder","type":"verb","definition":"think about if something is possible"},{"word":"concert","type":"common noun, singular","definition":"a public performance of music"},{"word":"pick up","type":"verb","definition":"go and get"},{"word":"popular","type":"Adjective","definition":"very well known and well liked"},{"word":"bucks","type":"common noun, plural","definition":"informal word for dollars"},{"word":"unfair","type":"Adjective","definition":"not fair, not just"}],"supplementary":[{"word":"suit","type":"common noun, singular","definition":"a set of clothes that usually consists of a jacket and a skirt or pair of pants that are made out of the same material"},{"word":"play","type":"common noun, singular","definition":"a piece of writing that tells a story through the actions and words of characters and that is performed on a stage"},{"word":"drop off","type":"phrase","definition":"to go and leave someone at a certain place"},{"word":"no way","type":"phrase","definition":"I cannot do it, it is not possible"},{"word":"lend","type":"principle verb, present simple","definition":"to give (something) to (someone) to be used for a period of time and then returned"}]}},{"id":50,"title":"New Guy In Town III","level":"Intermediate","mp3":"/audio/englishpod_0050pb.mp3","transcript":"https://ia600408.us.archive.org/10/items/englishpod_all/englishpod_0050.html","transcriptData":[{"speaker":"A","text":"Please make yourselves at home.    Let me take your coats. Dinner is almost ready;  I hope you brought your appetite"},{"speaker":"B","text":"Your house is lovely,   Armand!  Very interesting decor...very...Gothic."},{"speaker":"A","text":"It would be my pleasure. Please have a seat.    Can I offer you a glass of wine?"},{"speaker":"A","text":"Here you are. A very special merlot brought directly from my home country.  It has a unique ingredient which gives it a pleasant aroma and superior flavor."},{"speaker":"B","text":"It's a bit bitter for my taste... almost tastes like... like..."},{"speaker":"A","text":"Did she pass out?"},{"speaker":"A","text":"I hope that you didn't poison her drink too much!  You'll ruin our meal!"}],"vocabData":{"key":[{"word":"flavor","type":"common noun, singular","definition":"the way something tastes"},{"word":"aroma","type":"common noun, singular","definition":"the way something smells"},{"word":"pointer","type":"common noun, plural","definition":"tips, advice, hints"},{"word":"make yourselves at home","type":"phrase","definition":"get comfortable and feel like you are in your own home"},{"word":"good taste","type":"phrase","definition":"refers to liking nice things"},{"word":"bitter","type":"Adjective","definition":"opposite of sweet"},{"word":"pass out","type":"verb","definition":"faint, loss consciousness"}],"supplementary":[{"word":"sweet","type":"Adjective","definition":"containing a lot of sugar"},{"word":"have a seat","type":"phrase","definition":"sit down, take a seat"},{"word":"spike","type":"principle verb, present simple","definition":"to add alcohol or drugs to (food or drink)"},{"word":"sour","type":"Adjective","definition":"having an acid taste that is like the taste of a lemon"},{"word":"exclusive","type":"Adjective","definition":"only allowing in people from a high social class"}]}}];